import React, { useRef } from 'react';
import { plus, pluscircle, close } from './data'

export default (props) => {
  const { onChange, data } = props;
  let createRef = useRef();
  const dataRef = [
    useRef(),
    useRef(),
    useRef(),
    useRef(),
  ]

  // Function Upload
  const onUploadImage = (e, fld, idx) => {
    let file = e.target.files[0];
    const extentions = ['png', 'jpg', 'jpeg'];
    let reader = new FileReader(file);
    reader.onload = (evnt) => {
      let img = evnt.target.result;
      const dataFileExtention = img.split(';');
      const fileExtention = dataFileExtention[0].split('/')[1];
      let acceptExtention = false;
      let allowExtention = '';
      extentions.forEach((obj) => {
        obj === fileExtention ? (acceptExtention = true) : (allowExtention += obj + ', ');
      });
      if (acceptExtention) {
        onChange ?
          onChange(img, fld, idx) : alert('');
      } else {
        alert(`Image Extention not Allow (Allowed: ${allowExtention.substr(0, allowExtention.length - 2)})`);
      }
    }
    reader.readAsDataURL(file);
  }

  const onClear = (idx) => {
    onChange('', 'image', idx);
    onChange('', 'title', idx);
  }

  return (
    <div className="u-box">
      <div className="u-subtitle">Documentation Picture</div>
      <div className="u-row">
        {data && data[0].image !== '' ?
          <div className="u-box-upload-img" style={{ border: 'none' }}>
            <div className="u-box-close" onClick={() => onClear(0)}>
              <img src={close} width="20px" />
            </div>
            <div className="u-box-img-text">
              <input
                style={{ fontSize: 20 }}
                type="text"
                className="u-inputtext-img"
                placeholder="Title Image - Example: Arafah"
                value={data[0].title}
                onChange={(e) => onChange(e.target.value, 'title', 0)}
              />
            </div>
            <img
              style={{ height: '100%', borderRadius: 10 }}
              src={data[0].image}
            />
          </div>
          :
          <div onClick={() => createRef.current.click()} className="u-box-upload-img">
            <input type="file" onChange={(e) => onUploadImage(e, 'image', 0)} ref={createRef} hidden />
            <img width="30px" src={pluscircle} />
            <div className="u-choose-picture">Choose Picture</div>
          </div>
        }
        <div className="u-wrap-upload-img">
          {data && data.filter((obj, i) => i !== 0).map((item, i) => {
            return (
              <div className={i > 1 ? "u-box-upload-img small bottom" : "u-box-upload-img small top"} style={item.image !== '' ? { border: 'none' } : {}}>
                {item.image !== '' ?
                  <div className="u-box-close" onClick={() => onClear(i + 1)}>
                    <img src={close} width="20px" />
                  </div>
                  : []
                }
                {item.image !== '' ?
                  <div className="u-box-img-text">
                    <input
                      type="text"
                      className="u-inputtext-img"
                      placeholder="Title Image - Example: Arafah"
                      value={item.title}
                      onChange={(e) => onChange(e.target.value, 'title', i + 1)}
                    />
                  </div>
                  : []
                }
                <input type="file" onChange={(e) => onUploadImage(e, 'image', i + 1)} ref={dataRef[i]} hidden />
                <img
                  onClick={() => dataRef[i].current.click()}
                  src={item.image !== '' ? item.image : plus}
                  height="100%"
                  style={item.image !== '' ? {} : { padding: 55 }}
                />
              </div>
            )
          })}
        </div>

      </div>
    </div >
  )
}
