import React from 'react';
import { Row, Col, Button } from 'reactstrap';
import SubHeader from '../../../component/sub-header'
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";
import moment from "moment";
import TypeSchedule from '../../dashboard/component/dropdown'
import TimePicker from 'rc-time-picker'

const showSecond = true;

function onChange(value) {
}
export default class PushNotifications extends React.PureComponent {

  render() {
    return(
      <div className="section-search">
        <Row className="search-flight-heading" style={{paddingBottom: '40px'}}>New Notifications</Row>
        <Row className="step-form-table">
          <div style={{ width: '100%' }}>
            <SubHeader title={'New Notifications'} />
            <div className="form-search" style={{marginLeft: '10vw', marginRight: '10vw'}}>
              <div className="row">
                <input className="text-input" placeholder="Title Notification"/>
                <input className="text-input" placeholder="URL"/>
              </div>
              <div className="row" style={{marginTop: '1vw', marginLeft: 4}}>
                <textarea name="message" className="text-area" placeholder="Describe content here..."/>
              </div>
              <div style={{marginTop: '1vw', marginRight: '15vw'}}> 
                <Row>
                  <Col size={3}>
                    <p><b>Schedule</b></p>
                    <TypeSchedule schedule={true} />
                  </Col>
                  <Col size={3}>
                    <p><b>Date Upload</b></p>
                    <DatePicker
                      // selected={this.state.uploadDate}
                      // onChange={this.handleDateStart}
                      // minDate={moment()}
                      // style={{ padding: 20 }}
                      placeholderText="DD/MM/YYYY"
                      // dateFormat="DD/MM/YYYY"
                    />
                  </Col>
                  <Col size={4}>
                    <p><b>Time</b></p>
                    <TimePicker
                      style={{ width: 145 }}
                      showSecond={showSecond}
                      defaultValue={moment()}
                      onChange={onChange}
                      className="xxx"
                    />
                  </Col>


                </Row>
                <p style={{marginTop:'1vw'}}><b>User Segment</b></p>
                <div className="row"  style={{margin: 3, marginBottom: '4vw' }}>
                  <TypeSchedule segment={true} />
                </div>
              </div>
              <div><Button className="button-primary" style={{width: '14%'}}><b>UPLOAD</b></Button></div>
            </div>
          </div>
        </Row>
      </div>
    )
  }
}
