import React from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { Row, Col } from "reactstrap";
import { connect } from "react-redux";
// SCREEN
import Dashboard from "../dashboard/index";
import DataFlight from "../flight/index";
import DataUser from "../user/index";
import ChangePassword from "../change-password/index";
import Report from "../report/index";
import Setting from "../setting/index";
import Notifications from "../notification/index";
import PushNotifications from "../notification/push-notifications/index";
import Register from "../register/index";
import UserTraveler from "../user/traveler/index";
import "../../App.css";
import logo from "../../assets/png/logo-asitatrave.png";
import DataAgent from "../agents/index";
import Withdrawal from "../withdrawal/index";
import TopUp from "../top-up/index";
import Order from "../order/index";
import Tour from '../tour/index';
import Hotel from '../hotel/index'
import PackageTour from '../package-tour/index'
import Umrah from '../umrah'

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      hasreload: false,
      active: false
    };
    this.dataGroupRoleApi = this.generateGroupRoleApi();
    this.dataGenerateMenu = [
      {
        title: "Diagram Reporting",
        value: "dashboard",
        active: true,
        userRole: this.dataGroupRoleApi
      },
      {
        title: "Data Flight",
        value: "data-flight",
        active: false,
        userRole: this.dataGroupRoleApi
      },
      {
        title: "Data Hotel",
        value: "data-hotel",
        active: false,
        userRole: this.dataGroupRoleApi
      },
      {
        title: "Data Tour",
        value: "data-tour",
        active: false,
        userRole: this.dataGroupRoleApi
      },
      {
        title: "Package Tour",
        value: "package-tour",
        active: false,
        userRole: this.dataGroupRoleApi
      },
      {
        title: "Umrah",
        value: "umrah",
        active: false,
        userRole: this.dataGroupRoleApi
      },
      {
        title: "Data Users CMS",
        value: "data-user",
        active: false,
        userRole: [1]
      },
      {
        title: "Data Users Traveler",
        value: "user-traveler",
        active: false,
        userRole: [1]
      },
      {
        title: "Data Agents & Sub Agents",
        value: "data-agent",
        active: false,
        userRole: [1]
      },
      // {
      //   title: "Order History",
      //   value: "order-history",
      //   active: false,
      //   userRole: this.dataGroupRoleApi
      // },
      {
        title: "Data Top-Up",
        value: "top-up",
        active: false,
        userRole: [1]
      },
      {
        title: "Withdrawal",
        value: "withdrawal",
        active: false,
        userRole: [1]
      },
      // {
      //   title:'Setting',
      //   value:'setting',
      //   active:false,
      //   userRole:[1]
      // },
      {
        title: "Report",
        value: "report",
        active: false,
        userRole: [1]
      },
      // {
      //   title:'Push Notification',
      //   value:'notifications',ßß
      //   active:false,
      //   userRole:this.dataGroupRoleApi
      // },
      {
        title: "Change Password",
        value: "change-password",
        active: false,
        userRole: this.dataGroupRoleApi
      },
    ];
  }

  generateGroupRoleApi() {
    let _dataGroupRole = [];
    let { dataGroupRole } = this.props.global;
    if (dataGroupRole != null) {
      dataGroupRole.map(obj => {
        _dataGroupRole.push(obj.id);
      });
    }
    return _dataGroupRole;
  }
  handleLogout = () => {
    const { dispatch } = this.props;
    const { logOutAction } = this.props.loginActions;
    logOutAction(dispatch);
  };

  handleChangePages = pages => {
    this.setState({ active: false });
    this.props.history.push(pages);
  };

  handleFilterUserRole = obj => {
    const { userRole } = this.props.global;
    let checkUserAccessMenu = false;
    for (let j = 0; j < obj.userRole.length; j++) {
      if (userRole === obj.userRole[j]) {
        checkUserAccessMenu = true;
        break;
      }
    }
    if (checkUserAccessMenu) {
      return obj;
    }
  };
  dataGroupRoleApi = [];
  dataGenerateMenu = [];

  generateRenderMenuRole() {
    let routerActive = this.props.location.pathname.substring(1);
    return (
      <Col className="sectionSideBar" lg="3" md="3" sm="12" xs="12">
        <Row className="HeadingTitle" style={{ justifyContent: 'center', marginBottom: 10 }}>Menu Utama</Row>
        {this.dataGenerateMenu
          .filter(this.handleFilterUserRole.bind(this))
          .map((obj, i) => {
            if (routerActive == "home") {
              routerActive = "dashboard";
            }
            return (
              <Row key={i} className="sub-title">
                <Link
                  onClick={() => this.handleChangePages(obj.value)}
                  className={
                    // this.state.currentPage === obj.value
                    routerActive === obj.value ? "menuActive" : "menuNormal"
                  }
                  to={`/${obj.value}`}
                >
                  {obj.title}
                </Link>
              </Row>
            );
          })}
        {true ? (
          <Row className="sub-title">
            <div onClick={this.handleLogout} className="menuNormal">
              Log Out
              </div>
          </Row>
        ) : (
            []
          )}
      </Col>
    );
  }

  renderSwitchComponent() {
    var renderComponent = [];
    const { history } = this.props;
    let activeComponent = history.location.pathname.split("/")[1];
    switch (activeComponent) {
      case "dashboard":
        renderComponent = <Dashboard />;
        break;
      case "home":
        renderComponent = <Dashboard />;
        break;
      case "data-flight":
        renderComponent = <DataFlight />;
        break;
      case "data-user":
        renderComponent = <DataUser />;
        break;
      case "setting":
        renderComponent = <Setting />;
        break;
      case "report":
        renderComponent = <Report />;
        break;
      case "user-traveler":
        renderComponent = <UserTraveler />;
        break;
      case "data-agent":
        renderComponent = <DataAgent />;
        break;
      case "withdrawal":
        renderComponent = <Withdrawal />;
        break;
      case "top-up":
        renderComponent = <TopUp />;
        break;
      case "notifications":
        renderComponent = <Notifications />;
        break;
      case "register":
        renderComponent = <Register />;
        break;
      case "change-password":
        renderComponent = <ChangePassword />;
        break;
      case "order-history":
        renderComponent = <Order />;
        break;
      case "data-hotel":
        renderComponent = <Hotel />;
        break;
      case "data-tour":
        renderComponent = <Tour />;
        break;
      case "package-tour":
        renderComponent = <PackageTour />;
        break;
      case "umrah":
        renderComponent = <Umrah />;
        break;
      default:
        renderComponent = <div>Page Not Found !</div>;
    }
    return renderComponent;
  }

  openNav() {
    this.setState({ active: true });
  }
  closeNav() {
    this.setState({ active: false });
  }

  generateMenuMobile() {
    let routerActive = this.props.location.pathname.substring(1);
    return (
      <div>
        <div
          className={["sidenav", this.state.active ? "active" : ""].join(" ")}
        >
          <div
            className="closebtn"
            style={{ color: "#fd7e14" }}
            onClick={() => this.closeNav()}
          >
            &times;
          </div>
          {this.dataGenerateMenu
            .filter(this.handleFilterUserRole.bind(this))
            .map((obj, i) => {
              if (routerActive == "home") {
                routerActive = "dashboard";
              }
              return (
                <div style={{ textAlign: "-webkit-center" }}>
                  <Link
                    onClick={() => this.handleChangePages(obj.value)}
                    to={`/${obj.value}`}
                  >
                    {obj.title}
                  </Link>
                </div>
              );
            })}
          {true ? (
            <div
              style={{ color: "#fff", textAlign: "-webkit-center" }}
              onClick={this.handleLogout}
            >
              <a>Log Out</a>
            </div>
          ) : (
              []
            )}
        </div>
        <div className="headem">
          <div
            style={{ fontSize: 22, color: "#008c9a" }}
            onClick={() => this.openNav()}
          >
            &#9776;
          </div>
          <div style={{ width: "90%", textAlign: "center", padding: 3 }}>
            <img alt="Logo" src={!logo ? "" : logo} style={{ width: 80 }} />
          </div>
        </div>
      </div>
    );
  }

  renderMobile() {
    return (
      <div>
        {this.generateMenuMobile()}
        {this.renderSwitchComponent()}
      </div>
    );
  }

  render() {
    const { isLogin } = this.props.login;
    const { success, message } = this.props.umrah;
    if (isLogin == false) return [];
    if (window.screen.availWidth < 756) {
      return this.renderMobile();
    } else {
      return (
        <div>
          <div className={["u-modal", success ? 'hide' : ''].join(' ')}>
            <div className="u-title-subtitle"
              style={{ color: '#fff', textAlign: 'center', fontSize: 18 }}
            >{message}</div>
          </div>
          <Row>
            {this.generateRenderMenuRole()}
            <Col className="sectionContent" lg="9" md="9" sm="12" xs="12">
              {this.renderSwitchComponent()}
            </Col>
          </Row>
        </div>
      );
    }
  }
}

function mapStateToProps(state) {
  return {
    global: state.global
  };
}

export default connect(mapStateToProps)(Home);
