import React from 'react';
import { Row, Col, Button, Modal, ModalHeader, ModalBody, FormGroup } from "reactstrap";
import SubHeader from "../../component/sub-header";
import { PRIMARY_COLOR } from "../../config/constant";
import PaginationRemote from '../../component/PaginationRemote'
import { moneyFormat } from '../../utilities/helpers';
import moment from "moment";
import Dropdown from '../../component/dropdown';
import { PulseLoader } from "react-spinners";

export default class Withdrawal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      params: {
        apiKey: '4OGG3-U8RDM-29NXL-KYE6S-MWW15',
        filters: '',
        pageIndex: 1,
        itemsPerPage: 25,
        sizePerPage: 25,
        totalSize: 200,
      },
      modalOrder: false,
      selectDetail: null,
      status: "",
      change_status: [{ 'id': 'APPROVE', 'name': 'APPROVE' }, { 'id': 'REJECTED', 'name': 'REJECTED' }],
    };
    this.toggleOrder = this.toggleOrder.bind(this);
    this.togglePrimary = this.togglePrimary.bind(this);
    this.handleRole = this.handleRole.bind(this)
  }

  componentWillMount() {
    const { filters } = this.state.params
    this.loadDataPackageTour(filters)
  }
  loadDataPackageTour(filters, pageIndex, itemsPerPage) {
    const { dispatch } = this.props;
    let params = {
      apiKey: '4OGG3-U8RDM-29NXL-KYE6S-MWW15',
      filters: '',
      pageIndex: 1,
      itemsPerPage: 25
    }
    const { packageTourAction } = this.props.actions;
    packageTourAction(dispatch, params)
  }
  handleChangeSearch(value) {
    let { params } = this.state;
    params.filters = value;
    this.setState({ params });
    this.loadDataPackageTour(params.filters, params.pageIndex, params.itemsPerPage);
  }
  handleTableChange = (type, { pageIndex, sizePerPage }) => {
    let { params } = this.state;
    params.pageIndex = pageIndex;
    params.sizePerPage = sizePerPage;
    this.loadDataPackageTour(params.filters, params.pageIndex, params.itemsPerPage);
    this.setState({ params: params });
  };
  toggleOrder(row) {
    this.setState({ modalOrder: !this.state.modalOrder, selectDetail: row });

  }
  togglePrimary() {
    this.setState({ modal: !this.state.modal });
  }
  actionCol(inde, row) {
    return (
      <div className="center">
        {row.status === "WAITING_FOR_APPROVAL" ?
          <Button className="button-primary" onClick={() => this.toggleOrder(row)} >
            <i className="fa fa-eye fa-lg" ></i>
          </Button>
          :
          <Button className="button-primary" onClick={() => this.toggleOrder(row)} disabled>
            <i className="fa fa-eye fa-lg" ></i>
          </Button>
        }
      </div>
    );
  }
  handleRole(e) { this.setState({ status: e.target.value }) }
  handleCallbackStatus(callback) {
    const { dispatch, history } = this.props;
    const { setAlertMessage } = this.props.actionsAlert;
    if (callback.success) {
      setAlertMessage(dispatch, true, { title: 'INFO', message: "Status has been Change" });
      history.push('/home');
    } else {
      let _message = !callback.message ? 'Check your data' : callback.message;
      setAlertMessage(dispatch, true, { title: 'INFO', message: _message });
    }
  }
  submitStatus = () => {
    const { dispatch } = this.props;
    const { statusToursAction } = this.props.actions;
    const { setAlertMessage } = this.props.actionsAlert;
    const { id } = this.state.selectDetail
    if (this.state.status === "") {
      setAlertMessage(dispatch, true, { title: 'Info', messsage: "Select Status" })
    } else {
      let { status } = this.state
      let dataPost = {
        status: status
      }
      statusToursAction(dispatch, id, dataPost, this.handleCallbackStatus.bind(this))
      this.setState({ modalOrder: false });
    }
  }
  renderSubmit() {
    const { status } = this.state
    if (status == "") {
      return (
        <Row style={{ textAlign: 'start', marginTop: 20 }}>
          <Col xs="3" style={{ textAlign: 'start' }}><Button onClick={this.toggleOrder} style={{ backgroundColor: '#bebebe', borderColor: '#bebebe' }}><b>CANCEL</b></Button></Col>
          <Col xs="9">
            <Button onClick={() => this.submitStatus()} style={{ backgroundColor: '#bebebe', borderColor: '#bebebe' }} disabled>
              {(this.props.fetchingPackageTour === true) ?
                <PulseLoader sizeUnit={"px"} size={10} color={'white'} loading={true} /> : <b>SUBMIT</b>
              }
            </Button>
          </Col>
        </Row>
      )
    } else {
      return (
        <Row style={{ textAlign: 'start', marginTop: 20 }}>
          <Col xs="3" style={{ textAlign: 'start' }}><Button onClick={this.toggleOrder} style={{ backgroundColor: '#bebebe', borderColor: '#bebebe' }}><b>CANCEL</b></Button></Col>
          <Col xs="9"><Button style={{ backgroundColor: '#ea6520', borderColor: '#ea6520' }} onClick={() => this.submitStatus()}><b>SUBMIT</b></Button></Col>
        </Row>
      )
    }
  }
  renderStatus(id, row) {
    return (
      <div
        className={(!row ? "" : !row.status ? "" : row.status).toLowerCase()}
      >
        {!row ? "" : !row.status ? "" : row.status}
      </div>
    );
  }
  index = 0;
  columns = [
    { dataField: "index", text: "NO", headerStyle: { width: "3%" } },
    { dataField: "title", text: "TITLE", headerStyle: { width: "17%" } },
    { dataField: "price_adult", text: "PRICE", headerStyle: { width: "15%" }, formatter: (id, row) => moneyFormat(!row ? 0 : !row.price_adult ? 0 : row.price_adult) },
    { dataField: "created_at", text: "CREATED AT", headerStyle: { width: "13%" }, formatter: (id, row) => moment(row.created_at).format("DD-MM-YYYY hh:mm") },
    { dataField: "duration_days", text: "DAYS", headerStyle: { width: '5%' } },
    { dataField: "duration_night", text: "NIGHT", headerStyle: { width: '5%' } },
    // { dataField: "cut_of_time", text: "COT(days)", headerStyle: { width: '5%' } },
    { dataField: "status", text: "STATUS", headerStyle: { width: '10%' }, formatter: this.renderStatus.bind(this) },
    { dataField: "action", text: "ACTION", headerStyle: { width: "5%", textAlign: 'center' }, formatter: (id, row) => this.actionCol(id, row) }
  ]
  render() {
    const { params, selectDetail } = this.state;
    const { meta = { totalItems: 1 } } = this.props.packagetour;
    const { fetchingPackageTour } = this.props.packagetour
    let index = 0;
    let viewPackageTour = this.props.packagetour.dataPackageTour == null ? [] : this.props.packagetour.dataPackageTour;
    viewPackageTour.map((obj, i) => {
      index++;
      obj.index = params.pageIndex == 1 ? index : index + Number(params.pageIndex * params.sizePerPage) - 25;
    })
    return (
      <div className="section-search">
        <Row className="search-flight-heading bot"> List Package Tour </Row>
        <Row className="step-form-table">
          <div style={{ width: "100%" }}>
            <SubHeader title={"List Package Tour"} />
            <div className="form-search" >
              <PaginationRemote tableCustomers={true}
                loading={fetchingPackageTour}
                searchPlaceholder="Search by name"
                handleChangeSearch={this.handleChangeSearch.bind(this)}
                searchValue={params.filters}
                // dataexport= { filterData }
                data={viewPackageTour}
                columns={this.columns}
                page={params.pageIndex}
                sizePerPage={params.sizePerPage}
                totalSize={!meta ? 1 : meta.totalItems}
                onTableChange={this.handleTableChange}
              />
              <Modal style={{ paddingTop: 70 }}
                isOpen={this.state.modalOrder}
                togglePrimary={this.toggleOrder}
                className={"modal-primary" + this.props.className}
              >
                <ModalHeader style={{ background: PRIMARY_COLOR, color: "#ffffff" }} toggle={this.toggleOrder}>
                  STATUS TOUR
                </ModalHeader>
                <ModalBody style={{ marginTop: 30, marginBottom: 30, paddingTop: 0, paddingBottom: 0 }}>
                  <Row>
                    <Col className="show-tab" size={10}>
                      <FormGroup row>
                        <Col xs="12" md="12">
                          <Dropdown label="Status" id="status" data={this.state.change_status} onChange={this.handleRole} />
                        </Col>
                      </FormGroup>
                    </Col>
                  </Row>
                  {this.renderSubmit()}
                </ModalBody>
              </Modal>
            </div>
          </div>
        </Row>
      </div>
    )
  }
}
