import React from 'react';
import { Row, Col, Button, Modal, ModalHeader, ModalBody, Input } from "reactstrap";
import SubHeader from "../../component/sub-header";
import { PRIMARY_COLOR } from "../../config/constant";
import PaginationRemote from '../../component/PaginationRemote'
import { formatRupiah } from '../../utilities/helpers';
import moment from "moment";

export default class Withdrawal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modalDetails: false,
      modalReject: false,
      modalApprove: false,
      search: "",
      params: {
        q: '',
        page: 1,
        limit: 20,
        sizePerPage: 20,
        totalSize: 200,
        start_date: '',
        end_date: '',
        customer_id: '',
        status: '',
        withdrawal_type: '',
      },
      selectDetail: null,
      data: [],
      mobile: false,
      modal: false,
      search: "",
      notes: "",
      errorMessage: "",
    };
    this.togglePrimary = this.togglePrimary.bind(this);
    this.toggleDetails = this.toggleDetails.bind(this);
    this.toggleApprove = this.toggleApprove.bind(this);
    this.toggeleReject = this.toggeleReject.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentWillMount() {
    this.handleResetWithdrawalAction()
    const { q, page, limit, start_date, end_date, customer_id, status, withdrawal_type } = this.state.params
    this.loadDataListWithdrawal(q, page, limit, start_date, end_date, customer_id, status, withdrawal_type)
  }
  handleCallback() {
    const { fetchingListWithdrawal, history } = this.props
    if (fetchingListWithdrawal === true) {
      window.location.reload()
      alert("ACCEPT SUCCESS")
      history.push('/home');
    } else {
      this.setState({ errorMessage: "Error" })
    }
  }
  handleResetWithdrawalAction() {
    const { dispatch } = this.props;
    const { resetWithdrawal } = this.props.actions;
    resetWithdrawal(dispatch)
  }
  loadDataListWithdrawal(query, page, limit, startDate, endDate, idCustomer, status, wihtdrawalType) {
    const { dispatch } = this.props;
    let params = {
      q: query,
      page: page,
      limit: limit,
      start_date: startDate,
      end_date: endDate,
      customer_id: idCustomer,
      status: status,
      withdrawal_type: wihtdrawalType
    }
    const { withdrawalAction } = this.props.actions;
    let token = !this.props.login.token ? '' : this.props.login.token;
    withdrawalAction(dispatch, params, token)
  }
  togglePrimary() { this.setState({ modal: !this.state.modal }) }
  toggleDetails(row) { this.setState({ modalDetails: !this.state.modalDetails, selectDetail: row }) }
  toggleApprove(row) { this.setState({ modalApprove: !this.state.modalApprove, selectDetail: row }) }
  toggeleReject(row) { this.setState({ modalReject: !this.state.modalReject, selectDetail: row }) }
  handleChange(evt) { this.setState({ [evt.target.id]: evt.target.value }) }
  actionCol(id, row) {
    return (
      <div>
        <Button style={{ backgroundColor: '#ea6520', borderColor: '#ea6520' }} onClick={() => this.toggleDetails(row)}><i className="fa fa-eye fa-lg" ></i></Button>{' '}
        <Button color="primary" onClick={() => this.toggleApprove(row, id)}><i class="fa fa-check fa-lg"></i></Button>{' '}
        <Button color="danger" onClick={() => this.toggeleReject(row)}><i class="fa fa-times fa-lg" ></i></Button>
      </div>
    );
  }

  handleInputSearch(index) {
    const { data } = this.state;
    const { setSearching } = this.props.actionCommon
    let dataPost = {
      products: data[index].booking
    }
    setSearching(dataPost);
  }
  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page = page;
    params.sizePerPage = sizePerPage;
    this.loadDataListWithdrawal(params.q, params.page, params.limit, params.start_date, params.end_date, params.customer_id, params.status, params.withdrawal_type)
    this.setState({ params: params });
  }
  handleChangeSearch(value) {
    let { params } = this.state
    params.q = value
    this.setState({ params });
    this.loadDataListWithdrawal(params.q, params.page, params.limit, params.start_date, params.end_date, params.customer_id, params.status, params.withdrawal_type);
  }
  renderStatus(id, row) {
    return (
      <div className={(!row ? '' : !row.status ? '' : row.status).toLowerCase()}>{!row ? '' : !row.status ? '' : row.status}</div>
    )
  }
  indexList = 0;
  columns = [
    { dataField: "index", text: "NO", headerStyle: { width: "3%" } },
    { dataField: "created_at", text: "DATE", headerStyle: { width: "13%" }, formatter: (id, row) => moment(row.created_at).format("DD-MM-YYYY hh:mm") },
    { dataField: "agent_code", text: "ID", headerStyle: { width: "7%" } },
    { dataField: "customer_fullname", text: "NAME", headerStyle: { width: "22%" } },
    { dataField: "withdrawal_type", text: "TYPE", headerStyle: { width: "12%" } },
    { dataField: "amount", text: "AMOUNT", headerStyle: { width: "15%" }, formatter: (id, row) => formatRupiah(!row ? 0 : !row.amount ? 0 : row.amount) },
    { dataField: "status", text: "STATUS", headerStyle: { width: "10%" }, formatter: this.renderStatus.bind(this) },
    { dataField: "action", text: "ACTION", headerStyle: { width: "35%", textAlign: 'center' }, formatter: (id, row) => this.actionCol(id, row) },
  ]
  handleApprove = () => {
    const { approveWithdrawal } = this.props.actions
    const { dispatch } = this.props
    const { id } = this.state.selectDetail
    let dataPost = {
      notes: this.state.notes === null ? '-' : !this.state.notes ? '-' : this.state.notes
    }
    approveWithdrawal(dispatch, id, dataPost, this.handleCallback.bind(this))
    this.setState({ modalApprove: false });
  }
  handleReject = () => {
    const { rejectWithdrawal } = this.props.actions
    const { dispatch } = this.props
    const { id } = this.state.selectDetail
    let dataPost = {
      // notes: this.state.notes
      notes: this.state.notes === null ? '-' : !this.state.notes ? '-' : this.state.notes
    }
    rejectWithdrawal(dispatch, id, dataPost, this.handleCallback.bind(this))
    this.setState({ modalReject: false });
  }
  render() {
    const { params, selectDetail } = this.state;
    const { meta = { total: 1 } } = this.props.listwithdrawal;
    const { fetchingListWithdrawal } = this.props.listwithdrawal;
    let index = 0;
    let viewListWithdrawal = this.props.listwithdrawal.dataListWithdrawal == null ? [] : this.props.listwithdrawal.dataListWithdrawal;
    viewListWithdrawal.map((obj, i) => {
      index++;
      obj.index = params.page == 1 ? index : index + Number(params.page * params.sizePerPage) - 20;
    })

    return (
      <div className="section-search">
        <Row className="search-flight-heading bot"> Withdrawal </Row>
        <Row className="step-form-table">
          <div style={{ width: "100%" }}>
            <SubHeader title={"Withdrawal"} />
            <div className="form-search" >
              <PaginationRemote tableCustomers={true}
                loading={fetchingListWithdrawal}
                searchPlaceholder="Search by name"
                handleChangeSearch={this.handleChangeSearch.bind(this)}
                searchValue={params.q}
                // dataexport= { filterData }
                data={viewListWithdrawal}
                columns={this.columns}
                page={params.page}
                sizePerPage={params.sizePerPage}
                totalSize={!meta ? 1 : meta.total}
                onTableChange={this.handleTableChange}
              />
              <Modal isOpen={this.state.modalDetails} togglePrimary={this.toggleOrder} className={"modal-primary" + this.props.className}>
                <ModalHeader style={{ background: PRIMARY_COLOR, color: "#ffffff" }} toggle={this.toggleDetails}>
                  Details Withdrawal
                </ModalHeader>
                <ModalBody style={{ marginTop: 0, marginBottom: 0, paddingTop: 0, paddingBottom: 0 }}>
                  <Row style={{ marginBottom: 5, marginLeft: 20, marginTop: 30 }}>
                    <Col size={3}>Agent ID</Col>
                    <Col size={5} style={{ fontWeight: '800' }}>: {selectDetail === null ? '-' : !selectDetail.agent_code ? '-' : selectDetail.agent_code}</Col>
                  </Row>
                  <Row style={{ marginBottom: 5, marginLeft: 20 }}>
                    <Col size={3}>Account Number</Col>
                    <Col size={5} style={{ color: 'black' }}>: {selectDetail === null ? '-' : !selectDetail.account_no ? '-' : selectDetail.account_no}</Col>
                  </Row>
                  <Row style={{ marginBottom: 5, marginLeft: 20 }}>
                    <Col size={3}>Agent Name</Col>
                    <Col size={5} style={{ color: 'black' }}>: {selectDetail === null ? '-' : !selectDetail.customer_fullname ? '-' : selectDetail.customer_fullname}</Col>
                  </Row>
                  <Row style={{ marginBottom: 5, marginLeft: 20 }}>
                    <Col size={3}>Amount</Col>
                    <Col size={5} style={{ color: 'black' }}>: {selectDetail === null ? '-' : !selectDetail.amount ? '-' : selectDetail.amount}</Col>
                  </Row>
                  <Row style={{ marginBottom: 5, marginLeft: 20 }}>
                    <Col size={3}>Bank</Col>
                    <Col size={5} style={{ color: 'black' }}>: {selectDetail === null ? '-' : !selectDetail.account_name ? '-' : selectDetail.account_name}</Col>
                  </Row>
                  <Row style={{ marginBottom: 5, marginLeft: 20 }}>
                    <Col size={3}>Email</Col>
                    <Col size={5} style={{ color: 'black' }}>: {selectDetail === null ? '-' : !selectDetail.customer_email ? '-' : selectDetail.customer_email}</Col>
                  </Row>
                  <Row style={{ marginBottom: 5, marginLeft: 20 }}>
                    <Col size={3}>Date Update </Col>
                    <Col size={5} style={{ color: 'black' }}>: {moment(selectDetail === null ? '-' : !selectDetail.updated_at ? '-' : selectDetail.updated_at).format("YYYY-MM-DD")}</Col>
                  </Row>
                  <Row style={{ marginBottom: 5, marginLeft: 20, marginBottom: 30 }}>
                    <Col size={3}>Note</Col>
                    <Col size={5} style={{ color: 'black' }}>: {selectDetail === null ? '-' : !selectDetail.notes ? '-' : selectDetail.notes}</Col>
                  </Row>
                </ModalBody>
              </Modal>
              <Modal isOpen={this.state.modalApprove} togglePrimary={this.toggleApprove} className={"modal-primary" + this.props.className}>
                <ModalHeader style={{ background: PRIMARY_COLOR, color: "#ffffff" }} toggle={this.toggleApprove}>APPROVE</ModalHeader>
                <ModalBody style={{ marginTop: 35, marginBottom: 35, paddingTop: 0, paddingBottom: 0 }}>
                  <Col className="show-tab" size={10}>
                    <Row><i><b>If there are no notes, click the Button <span style={{ color: '#9cd99c' }}>Accept</span></b></i></Row>
                    <Row className="row" style={{ marginTop: '0.5vw' }}>
                      <Input type="text" id="notes" onChange={this.handleChange} placeholder="Describe Note Here..." />
                      {/* <div className="row" style={{marginTop: '1vw', marginLeft: 3}}>
                        <textarea id="notes" onChange={() => this.handleChange()} name="message" className="text-note" placeholder="Describe Note Here..."/>
                      </div> */}
                    </Row>
                    <Row className="row" style={{ marginTop: '1vw' }}>
                      <Button style={{ width: '25' }} className="button-register" onClick={() => this.handleApprove()}>
                        <b>ACCEPT</b>
                      </Button>
                    </Row>
                  </Col>
                </ModalBody>
              </Modal>
              <Modal isOpen={this.state.modalReject} togglePrimary={this.toggeleReject} className={"modal-primary" + this.props.className}>
                <ModalHeader style={{ background: PRIMARY_COLOR, color: "#ffffff" }} toggle={this.toggeleReject}>REJECT</ModalHeader>
                <ModalBody style={{ marginTop: 35, marginBottom: 35, paddingTop: 0, paddingBottom: 0 }}>
                  <Col className="show-tab" size={10}>
                    {/* <Row style={{marginBottom: 20, color: "#222222"}}><b>{selectDetail === null ? '-' : !selectDetail.customer_fullname ? '-' : selectDetail.customer_fullname}</b></Row> */}
                    <Row><i><b>If there are no notes, click the Button <span style={{ color: 'red' }}>Reject</span></b></i></Row>
                    <Row className="row" style={{ marginTop: '0.5vw' }}>
                      <Input type="text" id="notes" onChange={this.handleChange} placeholder="Describe Note Here..." />
                    </Row>
                    <Row className="row" style={{ marginTop: '1vw' }}>
                      <Button style={{ width: '25' }} className="button-register" onClick={() => this.handleReject()}>
                        <b>REJECT</b>
                      </Button>
                    </Row>
                  </Col>
                </ModalBody>
              </Modal>
            </div>
          </div>
        </Row>
      </div>
    )
  }
}
