import { DO_ORDER, LIST_ORDER_SUCCESS, LIST_ORDER_FAILED, RESET_ORDER_SUCCESS, ORDER_EXPORT, ORDER_EXPORT_SUCCESS, ORDER_EXPORT_FAILED } from '../../config/constant';

const INITIAL_STATE = {
  fetchingListOrder: false,
  dataListOrder: [],
  meta: null,
  dataOrder: []
}

export default function dataReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case DO_ORDER: {
      return {
        ...state,
        fetchingListOrder: true
      }
    }
    case LIST_ORDER_SUCCESS: {
      return {
        ...state,
        fetchingListOrder: false,
        dataListOrder: [...action.data],
        meta: action.meta
      }
    }
    case LIST_ORDER_FAILED: {
      return {
        ...state,
        fetchingListOrder: false
      }
    }
    case ORDER_EXPORT: {
      return {
        ...state,
        fetchingListOrder: true
      }
    }
    case ORDER_EXPORT_SUCCESS: {
      return {
        ...state,
        fetchingListOrder: false,
        dataOrder: action.data,
        meta: action.meta
      }
    }
    case ORDER_EXPORT_FAILED: {
      return {
        ...state,
        fetchingListOrder: false
      }
    }
    case RESET_ORDER_SUCCESS: {
      return {
        ...state,
        fetchingListOrder: false,
        dataListOrder: []
      }
    }
    default:
      return state
  }
}
