import React, { useState } from 'react';
import { useSelector, useDispatch, shallowEqual } from 'react-redux';
import { _createUmrah, clearErr, _listUmrah, _deleteUmrah, _editUmrah } from './redux/action';
import Content from './Content';
import Edit from './Edit';
import List from './List';

export default () => {
  // State
  const [isMode, setMode] = useState('list')
  const [isData, setData] = useState(null)

  // State Selector
  const { isLoading, token } = useSelector(state => ({
    isLoading: state.umrah.isLoading,
    token: state.login.token,
  }), shallowEqual)

  // Function Dispatch
  const dispatch = useDispatch()

  // Function
  const onMode = (type, id) => {
    if (type === 'edit') {
      setMode(type)
      _listUmrah(dispatch, id, onCallback)
    } else {
      setMode(type)
    }
  }
  const onCallback = (callback) => {
    if (callback.success) {
      setData(callback.data)
    }
  }

  return (
    <div>
      {isMode === 'list' ?
        <List
          onMode={onMode}
          dispatch={dispatch}
          onList={_listUmrah}
          onDelete={_deleteUmrah}
          isLoading={isLoading}
          token={token}
        />
        : isMode === 'add' ?
          <Content
            isMode={isMode}
            onMode={onMode}
            onCreate={_createUmrah}
            onClear={clearErr}
            dispatch={dispatch}
            isLoading={isLoading}
            token={token}
          />
          :
          <Edit
            isMode={isMode}
            onMode={onMode}
            data={isData}
            onEdit={_editUmrah}
            onClear={clearErr}
            dispatch={dispatch}
            isLoading={isLoading}
            token={token}
          />
      }
    </div>
  )
}
