import React from "react";
import { Container } from "reactstrap";
import "chartjs-plugin-datalabels";

export default class extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      selectData: [
        { index: 0, data: "Select Data", value: "" },
        { index: 1, data: "Total Sales", value: "Total Sales" },
        { index: 2, data: "Total Revenue", value: "Total Revenue" }
      ],
      selectAirline: [
        { index: 4, data: "All Airline", value: "" },
        { index: 1, data: "Garuda", value: "GA" },
        { index: 2, data: "Citilink", value: "Citilink" },
        { index: 3, data: "Sriwijaya", value: "Sriwijaya" }
      ],
      selectRoute: [
        { index: 0, data: "Domestic", value: "Domestic" },
        { index: 1, data: "International", value: "International" }
      ],
      selectProduct: [
        { index: 0, product: "Flight", value: "" },
        { index: 1, product: "Flight", value: "Flight" },
        { index: 2, product: "Hotel", value: "Hotel" }
      ],
      typeImage: [
        { index: 0, type: "Select Type", value: "" },
        { index: 1, type: "PROMO", value: "promo" },
        { index: 2, type: "SALE", value: "sale" }
      ],
      typeSchedule: [
        { index: 0, type: "Select Periode", value: "" },
        { index: 1, type: "Once", value: "" },
        { index: 2, type: "Weekly", value: "" },
        { index: 3, type: "Yearly", value: "" }
      ],
      typeSegment: [
        { index: 0, type: "Select Segment", value: "" },
        { index: 1, type: "All User", value: "" },
        { index: 2, type: "Facebook", value: "" },
        { index: 3, type: "Google", value: "" }
      ],
      selectFilter: [
        { index: 0, data: "All Data", value: "" },
        { index: 1, data: "Paid", value: "PAID" },
        { index: 2, data: "Booked", value: "BOOKED" },
        { index: 3, data: "Waiting Payment", value: "WAITING_PAYMENT" }
      ]
    };
  }

  handleSelectDropDown(selected) {
    const { handleSelect } = this.props;
    let value = !selected.target.value ? "" : selected.target.value;
    if (handleSelect) {
      handleSelect(value);
    }
  }

  render() {
    const {
      dataMonth,
      data,
      typeBanner,
      schedule,
      segment,
      selected = "",
      selectedProduct,
      selectAirline,
      dataRoute,
      dataPriode,
      filterData
    } = this.props;
    return (
      <div>
        {data ? (
          <Container>
            <div className="custom-select ">
              <select
                value={selected}
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 450,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -7
                }}
              >
                {this.state.selectData.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.data}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : selectedProduct ? (
          <Container>
            <div className="custom-select">
              <select
                value={selected}
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 170,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -7
                }}
              >
                {this.state.selectProduct.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.product}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : selectAirline ? (
          <Container>
            <div className="custom-select">
              <select
                value={selected}
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 170,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -7
                }}
              >
                {this.state.selectAirline.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.data}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : dataRoute ? (
          <Container>
            <div className="custom-select">
              <select
                value={selected}
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 170,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -7
                }}
              >
                {this.state.selectRoute.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.data}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : dataPriode ? (
          <Container>
            <div className="custom-select">
              <select
                value={selected}
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 170,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -7
                }}
              >
                {this.state.typeSchedule.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.type}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : dataMonth ? (
          <Container>
            <div className="custom-select ">
              <select
                value={selected}
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 450,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -7
                }}
              >
                {this.state.selectMonth.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.month}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : typeBanner ? (
          <Container>
            <div className="custom-select-type  ">
              <select
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 150,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -3
                }}
              >
                {this.state.typeImage.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.type}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : schedule ? (
          <Container>
            <div className="custom-select-type  ">
              <select
                style={{
                  width: 150,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -3
                }}
              >
                {this.state.typeSchedule.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.type}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : segment ? (
          <Container>
            <div className="custom-select-type  ">
              <select
                style={{
                  width: 150,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -3
                }}
              >
                {this.state.typeSegment.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.type}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : filterData ? (
          <Container>
            <div lassName="custom-select">
              <select
                value={selected}
                onChange={e => this.handleSelectDropDown(e)}
                style={{
                  width: 180,
                  height: 40,
                  marginLeft: -16,
                  marginTop: -7,
                  backgroundColor: "#ffffff"
                }}
              >
                {this.state.selectFilter.map((item, index) => (
                  <option class="grey-color" value={item.value}>
                    {item.data}
                  </option>
                ))}
              </select>
            </div>
          </Container>
        ) : (
                              <Container>
                                <div className="custom-select ">
                                  <select
                                    style={{
                                      width: 450,
                                      height: 40,
                                      marginLeft: -16,
                                      marginTop: -7
                                    }}
                                  >
                                    {this.state.selectProduct.map((item, index) => (
                                      <option class="grey-color" value={item.value}>
                                        {item.product}
                                      </option>
                                    ))}
                                  </select>
                                </div>
                              </Container>
                            )}
      </div>
    );
  }
}
