import { servicesCustomers, serviceAgentSub } from '../../../services/webServices';
import {
  DO_LIST_CUSTOMERS, LIST_CUSTOMERS_SUCCESS, LIST_CUSTOMERS_FAILED, RESET_CUSTOMERS_SUCCESS,
  DO_LOAD_ALL_CUSTOMERS, LOAD_ALL_CUSTOMERS_SUCCESS, LOAD_ALL_CUSTOMERS_FAILED, DO_CREATE_AGENTSUB, CREATE_AGENTSUB_SUCCESS, CREATE_AGENTSUB_FAILED, RESET_AGENTSUB_SUCCESS,
} from '../../../config/constant'


//CUSTOMER ACTION
export function customerAction(dispatch, params, token) {
  dispatch(doListCustomers())
  return servicesCustomers(params, token)
    .then(res => {
      dispatch(listCustomersSuccess(res.data, res.meta));
    })
    .catch(err => {
      return dispatch(listCustomersFailed(false))
    })
}
function doListCustomers() {
  return {
    type: DO_LIST_CUSTOMERS
  }
}
function listCustomersSuccess(payload, meta) {
  return {
    type: LIST_CUSTOMERS_SUCCESS,
    data: payload,
    meta: meta
  }
}
function listCustomersFailed() {
  return {
    type: LIST_CUSTOMERS_FAILED
  }
}

//CUSTOMERS ALL ACTION
export function loadAllCustomers(dispatch, params, token) {
  dispatch(doloadAllCustomers())
  return servicesCustomers(params, token)
    .then(res => {
      dispatch(loadAllCustomersSuccess(res.data, res.meta))
    })
    .catch(err => {
      return dispatch(loadAllCustomersFailed(false))
    })
}
function doloadAllCustomers() {
  return {
    type: DO_LOAD_ALL_CUSTOMERS
  }
}
function loadAllCustomersSuccess(payload, meta) {
  return {
    type: LOAD_ALL_CUSTOMERS_SUCCESS,
    data: payload,
    meta: meta
  }
}
function loadAllCustomersFailed() {
  return {
    type: LOAD_ALL_CUSTOMERS_FAILED
  }
}

//RESET LIST CUSTOMER
export function resetCustomersAction(dispatch) {
  dispatch(resetCustomersSuccess());
}
function resetCustomersSuccess(payload) {
  return {
    type: RESET_CUSTOMERS_SUCCESS,
    data: payload
  }
}

//AGENT & SUB AGENT ACTION
export function agentSubAction(dispatch, params, id, callback) {
  dispatch(doCreateAgentSub())
  return serviceAgentSub(params, id)
    .then(res => {
      setTimeout(() => {
        callback.call(this, res)
        dispatch(agentSubSuccess(res.data, res.meta));
      }, 3000)
    })
    .catch(err => {
      return dispatch(agentSubFailed(false))
    })
}
function doCreateAgentSub() {
  return {
    type: DO_CREATE_AGENTSUB
  }
}
function agentSubSuccess(payload, meta) {
  return {
    type: CREATE_AGENTSUB_SUCCESS,
    data: payload,
    meta: meta
  }
}
function agentSubFailed() {
  return {
    type: CREATE_AGENTSUB_FAILED
  }
}
export function resetAgentSubAction(dispatch) {
  dispatch(resetAgentSubSuccess());
}
function resetAgentSubSuccess(payload) {
  return {
    type: RESET_AGENTSUB_SUCCESS,
    dat: payload
  }
}
