import React from "react";
import { Row, Button } from "reactstrap";
import SubHeader from '../../../component/sub-header'
import moment from "moment"
import PaginationRemote from './../../../component/PaginationRemote'

export default class Traveler extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      params:{
        q:'',
        page:1,
        limit:20,
        sizePerPage: 20,
        totalSize:200
      },
      dataTraveler: [
        {
          index: "1", name: "Kitri Hapsari", phone: "1234556",email: "kitri3110@gmail.com", createdAt: "10-11-2019", status: "ACTIVE"
        }
      ]
    }
  }

  componentWillMount(){
    this.hendleResetCustomersAction();
    const { q, page, limit } = this.state.params;
    this.loadDataListCustomers(q, page, limit )
  }

  hendleResetCustomersAction(){
    const { dispatch } = this.props;
    const { resetCustomersAction } = this.props.actions;
    resetCustomersAction(dispatch)
  }
  loadDataListCustomers(query, page, limit){
    const { dispatch } = this.props;
    let params = {
      q: query,
      page: page,
      limit: limit
    }
    const { customerAction } = this.props.actions;
    // let token = !this.props.login ? '' : this.login.token;
    customerAction(dispatch, params)
  }

  handleInputSearch(index) {
    const { data } = this.state;
    const { setSearching } = this.props.actionsCommon;
    let dataPost = {
      products: data[index].booking
    };
    setSearching(dataPost);
  }
  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page = page;
    params.sizePerPage = sizePerPage;
    this.loadDataListCustomers(params.q, params.page, params.limit);
    this.setState({params:params})
  }
  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page=page;
    params.sizePerPage=sizePerPage;
    this.loadDataListCustomers(params.q, params.page, params.limit);
    this.setState({params:params});
  }
  handleChangeSearch(value){
    let { params } = this.state;
    params.q=value;
    this.setState({params});
    this.loadDataListCustomers(params.q, params.page, params.limit);
  }

  index=0
  indexList=0;
  columns = [
    // { dataField: "index", text: "NO", headerStyle: { width: "5%" } },
    { dataField: "fullname", text: "NAME", headerStyle: { width: "20%" } },
    { dataField: "mobileNo",text: "PHONE NUMBER", headerStyle: { width: "20%" } },
    { dataField: "email", text: "EMAIL", headerStyle: { width: "15%" } },
    { dataField: "birthDate", text: "BIRTH DATE", headerStyle: { width: "10%" } },
    { dataField: "createdAt", text: "DATE CREATE", headerStyle: { width: "15%" }, formatter:(id, row)=>moment(row.date).format("YYYY-MM-DD")},
  ];
  reloadDataUser = false;

  render() {
    const { params, selectDetail }  = this.state;
    const { meta= {total: 1}  } = this.props.customers;
    const { fetchingListCustomers } = this.props.customers;
    let index=0;
    let dataListCustomers =  this.props.customers.dataListAllCustomers==null ? [] : this.props.customers.dataListAllCustomers;
    let dataViewListCustomers = this.props.customers.dataListCustomers == null ? [] : this.props.customers.dataListCustomers;
    dataViewListCustomers.map((obj,i)=>{
      index++;
      obj.index= params.page===1 ? index : index+Number(params.page*params.sizePerPage)-20;
    });
    return(
      <div lassName="section-search">
        <Row className="search-flight-heading"  style={{ paddingBottom: "40px" }} >
          Data Traveler
        </Row>
        <Row className="step-form-table">
          <div style={{ width: "100%" }}>
          <SubHeader title={"Data Traveler"} />
            <div className="form-search">
            <PaginationRemote tableCustomers={true}
              loading = {fetchingListCustomers}
              searchPlaceholder="Search in Here"
              handleChangeSearch={this.handleChangeSearch.bind(this)}
              searchValue={params.q}
              // dataexport={dataListCustomers}
              data={ dataListCustomers }
              columns={this.columns}
              page={ params.pageOptions }
              sizePerPage={ params.sizePerPage }
              totalSize={ !meta.total ? 1 : meta.total }
              onTableChange={ this.handleTableChange }
            />
            </div>
          </div>
        </Row>
      </div>
    )
  }
}
