import { RESET_PACKAGE_TOUR, GET_PACKAGE_TOUR, PACKAGE_TOUR_SUCCESS, PACKAGE_TOUR_FAILED, POST_STATUS_TOUR, STATUS_TOUR_SUCCESS, STATUS_TOUR_FAILED } from '../../config/constant.js';

const INITIAL_STATE = {
  fetchingPackageTour: false,
  fetchingStatusTour: false,
  dataStatusTour: null,
  dataPackageTour: [],
  meta: null,
  err: null
}

export default function dataReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
    case GET_PACKAGE_TOUR: {
      return {
        ...state,
        fetchingPackageTour: true
      }
    }
    case PACKAGE_TOUR_SUCCESS: {
      return {
        ...state,
        fetchingPackageTour: false,
        dataPackageTour: [...action.data],
        meta: action.meta
      }
    }
    case PACKAGE_TOUR_FAILED: {
      return {
        ...state,
        fetchingPackageTour: false
      }
    }
    case POST_STATUS_TOUR: {
      return {
        ...state,
        fetchingStatusTour: true,
        dataStatusTour: action.data
      }
    }
    case STATUS_TOUR_SUCCESS: {
      return {
        ...state,
        fetchingStatusTour: false,
        dataStatusTour: action.data
      }
    }
    case STATUS_TOUR_FAILED: {
      return {
        ...state,
        fetchingStatusTour: false
      }
    }
    case RESET_PACKAGE_TOUR: {
      return {
        ...state,
        fetchingPackageTour: false,
        dataPackageTour: []
      }
    }
    default:
      return state
  }
}
