import React from "react";
import { Row, Button, Modal, ModalHeader, ModalBody } from "reactstrap";
import Tabs from "../../component/tabs";
import InfoPassenger from "./component/info-passenger";
import DetailsTour from "./component/tour-details";
import DetailsHotel from './component/hotel-details'
import Transportation from './component/flight-details';
import SubHeader from "../../component/sub-header";
import { PRIMARY_COLOR } from "../../config/constant";
import PaginationRemote from "./../../component/PaginationRemote";
import moment from "moment";
import { formatRupiah } from "./../../utilities/helpers";

export default class dataOrder extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      params: {
        q: '',
        page: 1,
        limit: 20,
        sizePerPage: 20,
        totalSize: 200,
        start_date: '',
        end_date: '',
        customer_id: '',
        status: '',
        product_code: 'TOUR'
      },
      selectDetail: null,
      selectInfo: null,
      modalOrder: false,
      modalDetails: false,
      modal: false,
      search: "",
    };
    this.togglePrimary = this.togglePrimary.bind(this);
    this.toggleOrder = this.toggleOrder.bind(this);
    this.toggleDetails = this.toggleDetails.bind(this)
    this.actionCol = this.actionCol.bind(this);
  }
  componentWillMount() {
    const { q, page, limit, start_date, end_date, customer_id, status, product_code } = this.state.params
    this.loaddataOrder(q, page, limit, start_date, end_date, customer_id, status, product_code)
    this.downloadData()
  }
  loaddataOrder(query, page, limit, start_date, customer_id, status) {
    const { dispatch } = this.props;
    let params = {
      q: query,
      page: page,
      limit: limit,
      start_date: start_date,
      end_date: start_date,
      customer_id: customer_id,
      status: status,
      product_code: 'TOUR',
    }
    const { orderAction } = this.props.actions;
    orderAction(dispatch, params)
  }
  downloadData() {
    const { dispatch } = this.props;
    let params = {
      q: "",
      page: 1,
      limit: !this.props.order.meta ? 20 : this.props.order.meta.total,
      start_date: "",
      end_date: "",
      customer_id: "",
      status: "",
      product_code: 'TOUR'
    }
    const { exportOrderAction } = this.props.actions;
    exportOrderAction(dispatch, params)
  }
  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page = page;
    params.sizePerPage = sizePerPage;
    this.loaddataOrder(params.q, params.page, params.limit, params.customer_id, params.end_date, params.start_date, params.status, params.product_code);
    this.setState({ params: params });
  }
  handleChangeSearch(value) {
    let { params } = this.state;
    params.q = value;
    this.setState({ params });
    this.loaddataOrder(params.q, params.page, params.limit, params.customer_id, params.end_date, params.start_date, params.status, params.product_code);
  }
  togglePrimary() {
    this.setState({ modal: !this.state.modal });
  }
  toggleOrder(row) {
    this.setState({ modalOrder: !this.state.modalOrder, selectDetail: row, });
  }
  toggleDetails(row) {
    if (row.flight_data === null && row.hotel_data === null) {
      this.setState({ modalDetails: this.state.modalDetails, selectInfo: row });
    } else {
      this.setState({ modalDetails: !this.state.modalDetails, selectInfo: row });
    }
  }
  actionCol(id, row) {
    return (
      <div>
        <Button onClick={() => this.toggleOrder(row)} className="button-primary"><i className="fa fa-eye fa-lg" ></i></Button> {' '}
        {row.flight_data === null && row.hotel_data === null ?
          <Button onClick={() => this.toggleDetails(row)} className="button-primary" disabled><i className="fa fa-info-circle fa-lg" ></i></Button>
          :
          <Button onClick={() => this.toggleDetails(row)} className="button-primary"><i className="fa fa-info-circle fa-lg" ></i></Button>
        }
      </div>
    );
  }
  renderStatusTour(id, row) {
    return (
      <div
        className={(!row ? "" : !row.status ? "" : row.status).toLowerCase()}
      >
        {!row ? "" : !row.status ? "" : row.status}
      </div>
    );
  }
  columns = [
    { dataField: "index", text: "NO", headerStyle: { width: "3%" } },
    { dataField: "booking_code", text: "BOOKING", headerStyle: { width: "10%" } },
    { dataField: "contact_email", text: "EMAIL AGENT", headerStyle: { width: "10%" } },
    { dataField: "contact_phone", text: "PHONE AGENT", headerStyle: { width: "12%" } },
    { dataField: "created_at", text: "DATE", formatter: (id, row) => moment(row.created_at).format("DD-MM-YYYY hh:mm"), headerStyle: { width: "15%" } },
    { dataField: "total_amount", text: "AMOUNT", formatter: (id, row) => formatRupiah(!row ? 0 : !row.total_amount ? 0 : row.total_amount), headerStyle: { width: "15%" } },
    { dataField: "status", text: "STATUS", formatter: this.renderStatusTour.bind(this), headerStyle: { width: "10%" } },
    { dataField: "action", text: "ACTION", formatter: (id, row) => this.actionCol(id, row), headerStyle: { width: "20%", textAlign: 'center' } }
  ];

  render() {
    const { params, selectDetail, selectInfo } = this.state;
    const { fetchingListOrder, dataListOrder } = this.props.order
    const { meta = { total: 1 } } = this.props.order;
    const groupOrder = dataListOrder.reduce((acc, el) => {
      const tmp = {
        id: el.id,
        flight: el.flight_data,
        hotel: el.hotel_data,
        rail: el.rail_data,
        tour: el.tour_data,
      }
      acc.push(tmp)
      return acc
    }, [])
    let exportData = this.props.order.dataOrder == null ? [] : this.props.order.dataOrder;
    let filterData = exportData.filter(exportData => exportData.status == "PAID")
    let no = 0;
    filterData.map((obj, i) => {
      no++;
      obj.index = no;
    })
    let viewListTour = this.props.order.dataListOrder == null ? [] : this.props.order.dataListOrder;
    let index = 0;
    viewListTour.map((obj, i) => {
      index++;
      obj.index = params.page === 1 ? index : index + Number(params.page * params.sizePerPage) - 20;
    })
    return (
      <div className="section-search">
        <Row className="search-flight-heading bot">Data Tour</Row>
        <Row className="step-form-table">
          <div style={{ width: "100%" }}>
            <SubHeader title={"Data Tour"} />
            <div className="form-search">
              <PaginationRemote
                download={filterData == null ? false : true}
                searchPlaceholder="Search Booking ID"
                loading={fetchingListOrder}
                handleChangeSearch={this.handleChangeSearch.bind(this)}
                searchValue={params.q}
                data={viewListTour}
                dataexport={filterData}
                columns={this.columns}
                page={params.page}
                sizePerPage={params.sizePerPage}
                totalSize={!meta ? 1 : meta.total}
                onTableChange={this.handleTableChange}
              />
              <Modal isOpen={this.state.modalOrder} togglePrimary={this.toggleOrder} className={"modal-primary " + this.props.className} >
                <ModalHeader style={{ background: PRIMARY_COLOR, color: "#ffffff" }} toggle={this.toggleOrder}>
                  Details Itinerary
                </ModalHeader>
                <ModalBody style={{ marginTop: 0, marginBottom: 0, paddingTop: 0, paddingBottom: 0 }}>
                  <Row className="show-tab">
                    <Tabs>
                      <DetailsTour selectDetail={selectDetail} title="Tour" />
                      <InfoPassenger selectDetail={selectDetail} title="Passenger" />
                    </Tabs>
                  </Row>
                </ModalBody>
              </Modal>
              <Modal isOpen={this.state.modalDetails} togglePrimary={this.toggleDetails} className={"modal-primary " + this.props.className} >
                <ModalHeader style={{ background: PRIMARY_COLOR, color: "#ffffff" }} toggle={this.toggleDetails}>
                  Details Transportation & Hotel
                </ModalHeader>
                <ModalBody style={{ marginTop: 0, marginBottom: 0, paddingTop: 0, paddingBottom: 0 }}>
                  <Row className="show-tab">
                    <Tabs>
                      <Transportation selectInfo={selectInfo} title="Transportation" />
                      <DetailsHotel selectInfo={selectInfo} title="Hotel" />
                    </Tabs>
                  </Row>
                </ModalBody>
              </Modal>
            </div>
          </div>
        </Row>
      </div>
    );
  }
}
