import { servicesListFlight } from '../../services/webServices';
import { DO_LIST_FLIGHT, LIST_FLIGHT_SUCCESS, LIST_FLIGHT_FAILED , RESET_FLIGHT_SUCCESS, DO_LIST_FLIGHT1, LIST_FLIGHT_FAILED1, LIST_FLIGHT_SUCCESS1 } from '../../config/constant';

//FLIGHT ACTION
export function flightAction(dispatch, params, token) {
  dispatch(doListFlight())
  return servicesListFlight(params, token)
  .then( res => {
    dispatch(listFlightSuccess(res.data, res.meta));
  })
  .catch(err => {
    return dispatch(listFlightFailed(false))
  })
}
function doListFlight() {
  return {
    type: DO_LIST_FLIGHT
  }
}
function listFlightSuccess(payload, meta) {
  return {
    type: LIST_FLIGHT_SUCCESS,
    data: payload,
    meta: meta
  }
}
function listFlightFailed() {
  return {
    type: LIST_FLIGHT_FAILED
  }
}


export function flightAction1(dispatch, params, token) {
  dispatch(doListFlight1())
  return servicesListFlight(params, token)
  .then( res => {
    dispatch(listFlightSuccess1(res.data, res.meta));
  })
  .catch(err => {
    return dispatch(listFlightFailed1(false))
  })
}
function doListFlight1() {
  return {
    type: DO_LIST_FLIGHT1
  }
}
function listFlightSuccess1(payload, meta) {
  return {
    type: LIST_FLIGHT_SUCCESS1,
    data: payload,
    meta: meta
  }
}
function listFlightFailed1() {
  return {
    type: LIST_FLIGHT_FAILED1
  }
}

//RESET LIST FLIGHT
export function resetFlightAction(dispatch) {
  dispatch(resetListFlightSuccess());
}
function resetListFlightSuccess(payload) {
  return {
    type: RESET_FLIGHT_SUCCESS,
    data: payload
  }
}
