import React from "react";
import { Row, Button } from "reactstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import SubHeader from "../../component/sub-header";
import { Link } from "react-router-dom";

export default class DataUser extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      modalOrder: false,
      search: "",
      products: [
        {
          id: "1",
          title: "New Version",
          content: "https://apps.xxxx.com",
          schedule: "Once 19/03/2019 08:30 PM",
          user_segment: "All User"
        },
        {
          id: "2",
          title: "Promo",
          content: "Ada diskon di Aeroja 30% all route",
          schedule: "Weekly 19/03/2019 08:30 PM",
          user_segment: "Facebook"
        },
        {
          id: "3",
          title: "Update",
          content: "https://apps.xxxx.com",
          schedule: "Weekly 19/03/2019 08:30 PM",
          user_segment: "Google"
        },
        {
          id: "4",
          title: "Promo",
          content: "https://apps.xxxx.com",
          schedule: "Monthly 19/03/2019 08:30 PM",
          user_segment: "Facebook"
        },
        {
          id: "6",
          title: "Promo",
          content: "https://apps.xxxx.com",
          schedule: "Weekly 19/03/2019 08:30 PM",
          user_segment: "Facebook"
        }
      ]
    };
  }

  render() {
    const { SearchBar } = Search;
    const columns = [
      { dataField: "id", text: "NO", headerStyle: { width: "5%" } },
      { dataField: "title", text: "TITLE", headerStyle: { width: "15%" } },
      { dataField: "content", text: "CONTENT", headerStyle: { width: "35%" } },
      { dataField: "schedule", text: "SCHEDULE", headerStyle: { width: "30%" } },
      { dataField: "user_segment",text: "USER SEGMENT", headerStyle: { width: "15%" }}
    ];
    const pageOptions = {
      paginationSize: 10,
      pageStartIndex: 1,
      withFirstAndLast: false,
      hideSizePerPage: true,
      hidePageListOnlyOnePage: true
    };
    return (
      <div className="section-search">
        <Row
          className="search-flight-heading"
          style={{ paddingBottom: "40px" }}
        >
          Notifications
        </Row>
        <Row className="step-form-table">
          <div style={{ width: "100%" }}>
            <SubHeader title={"Notifications"} />
            <div className="form-search">
              <ToolkitProvider
                keyField="id"
                data={this.state.products}
                columns={columns}
                search
              >
                {props => (
                  <div style={{ marginTop: 10, marginBottom: 50 }}>
                    <div className="row">
                      <SearchBar
                        className="custome-search-field"
                        placeholder="Search In Here"
                        {...props.searchProps}
                      />
                      <div style={{ marginLeft: "3vw" }}>
                        <Link to={"/push-notifications"}>
                          <Button
                            onClick={this.toggleOrder}
                            style={{
                              backgroundColor: "#a2195b",
                              borderColor: "#a2195b"
                            }}
                          >
                            ADD NOTIFICATIONS
                          </Button>
                        </Link>
                      </div>
                    </div>
                    <hr />
                    <BootstrapTable
                      pagination={paginationFactory(pageOptions)}
                      striped
                      {...props.baseProps}
                    />
                  </div>
                )}
              </ToolkitProvider>
            </div>
          </div>
        </Row>
      </div>
    );
  }
}
