import { servicesChangePassword } from "./../../services/webServices"
import { DO_CHANGE_PASSWORD, CHANGE_PASSWORD_SUCCESS, CHANGE_PASSWORD_FAILED } from "./../../config/constant"

// CHANGE PASSWORD ACTION
export function changePasswordAction(dispatch, id, data, callback) {
  dispatch(doChangePassword());
  return servicesChangePassword(id, data)
  .then(res => {
    dispatch(changePasswordSuccess(res));
    callback.call(this,res);
  })
  .catch(() => {
    return dispatch(changePasswordFailed(false))
  })
}

function doChangePassword() {
  return {
    type:  DO_CHANGE_PASSWORD
  };
}

function changePasswordSuccess(payload) {
  return {
    type: CHANGE_PASSWORD_SUCCESS,
    data: payload
  }
}

function changePasswordFailed() {
  return {
    type: CHANGE_PASSWORD_FAILED
  }
}
