import React from 'react';
import CurrencyInput from 'react-currency-input';

export default (props) => {
  const { name, value, onChange, placeholder, type, disable } = props;
  return (
    <div className="u-box">
      <div className="u-subtitle">{name}</div>
      {type === "price" ?
        <div className="u-title-subtitle">
          <div className="u-title-subtitle" style={{ marginTop: 15 }}>Single Bed</div>
          Rp.
          <CurrencyInput
            className="u-inputtext"
            thousandSeparaator={'.'}
            precision={0}
            value={value && value[0] && value[0].adult_single}
            onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'adult_single', 0)}
          />
          <div className="u-title-subtitle" style={{ marginTop: 15 }}>Twin Bed</div>
          Rp.
          <CurrencyInput
            className="u-inputtext"
            thousandSeparaator={'.'}
            precision={0}
            value={value && value[1] && value[1].adult_twin}
            onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'adult_twin', 1)}
          />
          <div className="u-title-subtitle" style={{ marginTop: 15 }}>Triple Bed</div>
          Rp.
          <CurrencyInput
            className="u-inputtext"
            thousandSeparaator={'.'}
            precision={0}
            value={value && value[2] && value[2].adult_triple}
            onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'adult_triple', 2)}
          />
          <div className="u-title-subtitle" style={{ marginTop: 15 }}>Quad Bed</div>
          Rp.
          <CurrencyInput
            className="u-inputtext"
            thousandSeparaator={'.'}
            precision={0}
            value={value && value[3] && value[3].adult_quad}
            onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'adult_quad', 3)}
          />
          <div className="u-title-subtitle" style={{ marginTop: 15 }}>Double Bed</div>
          Rp.
          <CurrencyInput
            className="u-inputtext"
            thousandSeparaator={'.'}
            precision={0}
            value={value && value[4] && value[4].adult_double}
            onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'adult_double', 4)}
          />
        </div>
        : type === 'addon' ?
          <div className="u-title-subtitle">
            <div className="u-title-subtitle" style={{ marginTop: 15 }}>Visa</div>
            Rp.
            <CurrencyInput
              className="u-inputtext"
              thousandSeparaator={'.'}
              precision={0}
              value={value && value.visa}
              onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'visa', 0)}
            />
            <div className="u-title-subtitle" style={{ marginTop: 15 }}>Full (Equipment, Handling, Manasik & Insurance)</div>
            Rp.
            <CurrencyInput
              className="u-inputtext"
              thousandSeparaator={'.'}
              precision={0}
              value={value && value.full}
              onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'full', 1)}
            />
            <div className="u-title-subtitle" style={{ marginTop: 15 }}>Transaction Fee</div>
            Rp.
            <CurrencyInput
              className="u-inputtext"
              thousandSeparaator={'.'}
              precision={0}
              value={value && value.none}
              onChangeEvent={disable ? () => { } : (evnt, mskv, fltv) => onChange(fltv, 'none', 2)}
            />
          </div>
          : <input
            type="text"
            className="u-inputtext"
            placeholder={placeholder}
            value={value}
            onChange={disable ? () => { } : (e) => onChange(e.target.value)}
          />
      }
    </div >
  )
}
