import React, { useState } from 'react';
import './umrah.css';
import Field from './components/Field';
import FieldAdd from './components/FieldAdd';
import FieldTextEditor from './components/FieldTextEditor';
import TopImage from './components/TopImage';
import DocPicture from './components/DocPicture';
import moment from 'moment';
import Loading from '../../component/Loading';

export default (props) => {
  // Props
  const { onCreate, dispatch, onClear, token, isLoading, onMode } = props;

  // State
  const [bh, setBh] = useState('')
  const [isTitle, setTitle] = useState('')
  const [isThumbnail, setThumbnail] = useState('')
  const [isCoT, setCoT] = useState('')
  const [isDuration, setDuration] = useState('')
  const [isQuota, setQuota] = useState('')
  const [isMinPax, setMinPax] = useState('')
  const [isTerms, setTerms] = useState('')
  const [isPolicy, setPolicy] = useState('')
  const [isTripDates, setTripDates] = useState([
    {
      startDate: moment(new Date).add(7, 'days').format('YYYY-MM-DD'),
      endDate: moment(new Date).add(10, 'days').format('YYYY-MM-DD')
    }
  ]);
  const [isVisited, setVisited] = useState([{ name: '' }]);
  const [isInclude, setInclude] = useState([{ title: '' }]);
  const [isExclude, setExclude] = useState([{ title: '' }]);
  const [isItinerary, setItinerary] = useState([{
    day: 1,
    title: "",
    description: "",
    facilities: []
  }]);
  const [isMedia, setMedia] = useState([
    { title: "", image: "" },
    { title: "", image: "" },
    { title: "", image: "" },
    { title: "", image: "" },
    { title: "", image: "" }
  ]);
  const [isPriceConfig, setPriceConfig] = useState([
    { adult_single: 0 },
    { adult_twin: 0 },
    { adult_triple: 0 },
    { adult_quad: 0 },
    { adult_double: 0 },
  ]);
  const [isAddOn, setAddOn] = useState({
    visa: 0,
    full: 0,
    none: 0,
  });

  // Function Add
  const addTripDates = () => {
    let tripDates = [];
    tripDates.push({
      startDate: moment(new Date).add(7, 'days').format('YYYY-MM-DD'),
      endDate: moment(new Date).add(10, 'days').format('YYYY-MM-DD')
    });
    setTripDates(isTripDates.concat(tripDates));
  }
  const addVisited = () => {
    let visited = [];
    visited.push({ name: '' });
    setVisited(isVisited.concat(visited));
  }
  const addInclude = () => {
    let include = [];
    include.push({ title: '' });
    setInclude(isInclude.concat(include));
  }
  const addExclude = () => {
    let exclude = [];
    exclude.push({ title: '' });
    setExclude(isExclude.concat(exclude));
  }
  const addItinerary = (index) => {
    let itinerary = [];
    itinerary.push({
      day: index + 1,
      title: "",
      description: "",
      facilities: []
    });
    setItinerary(isItinerary.concat(itinerary));
  }
  const addThumbnail = (value) => {
    setThumbnail(value)
  }

  // Function Delete
  const delTripDates = (index) => {
    isTripDates.splice(index, 1);
    setBh(new Date().getUTCMilliseconds())
  }
  const delVisited = (index) => {
    isVisited.splice(index, 1);
    setBh(new Date().getUTCMilliseconds())
  }
  const delInclude = (index) => {
    isInclude.splice(index, 1);
    setBh(new Date().getUTCMilliseconds())
  }
  const delExclude = (index) => {
    isExclude.splice(index, 1);
    setBh(new Date().getUTCMilliseconds())
  }
  const delItinerary = (index) => {
    isItinerary.splice(index, 1);
    setBh(new Date().getUTCMilliseconds())
  }

  // Function Change Text
  const onChangeTitle = (text) => {
    setTitle(text);
  }
  const onChangeDate = (field, date, i) => {
    isTripDates[i][field] = moment(date).format('YYYY-MM-DD');
    setBh(new Date().getUTCMilliseconds());
  }
  const onChangeCoT = (text) => {
    setCoT(parseInt(text));
  }
  const onChangeDuration = (text) => {
    setDuration(parseInt(text));
  }
  const onChangeQuota = (text) => {
    setQuota(parseInt(text));
  }
  const onChangeMinPax = (text) => {
    setMinPax(parseInt(text));
  }
  const onChangePrice = (text, field, i) => {
    isPriceConfig[i][field] = text;
  }
  const onChangeAddOn = (text, field, i) => {
    isAddOn[field] = text;
  }
  const onChangeVisited = (text, i) => {
    isVisited[i]['name'] = text;
    setBh(new Date().getUTCMilliseconds());
  }
  const onChangeInclude = (text, i) => {
    isInclude[i]['title'] = text;
    setBh(new Date().getUTCMilliseconds());
  }
  const onChangeExclude = (text, i) => {
    isExclude[i]['title'] = text;
    setBh(new Date().getUTCMilliseconds());
  }
  const onChangeItinerary = (text, i, field) => {
    isItinerary[i][field] = text;
    setBh(new Date().getUTCMilliseconds());
  }
  const onChangeTerms = (text) => {
    setTerms(text);
  }
  const onChangePolicy = (text) => {
    setPolicy(text);
  }
  const onChangeMedia = (value, field, i) => {
    isMedia[i][field] = value;
    setBh(new Date().getUTCMilliseconds())
  }

  // Function Submit
  const onSubmit = () => {
    let payload = {
      title: isTitle,
      topImage: isThumbnail,
      thumbnailImage: isThumbnail,
      duration_days: isDuration,
      cut_of_time: isCoT,
      quotaPerson: isQuota,
      price_config: isPriceConfig,
      umrah_addons: isAddOn,
      info_excluded: isExclude,
      info_included: isInclude,
      terms_conditions: [isTerms],
      cancellation_policy: [isPolicy],
      accomodation_flight: false,
      accomodation_hotel: true,
      accomodation_bus: true,
      price_adult: isPriceConfig[0].adult_single,
      price_child: isPriceConfig[0].adult_single,
      min_pax: isMinPax,
      max_pax: -1,
      tour_itineraries: isItinerary,
      media: isMedia,
      tour_trip_dates: isTripDates,
      visit_cities: isVisited,
      mr_countries: 100,
      info_hotel: null,
    }
    onCreate(dispatch, payload, token, onCallback);
  }
  const onCallback = (callback) => {
    if (callback.success) {
      onClear(dispatch, false, 'Saved Successfully.')
    } else {
      onClear(dispatch, false, 'Please enter all the data correctly.')
    }
    setTimeout(() => {
      onClear(dispatch, true, 'Please enter all the data correctly.')
    }, 5000);
  }

  return (
    <div className="u-wrapper">
      <div className="u-title u-row space-between">
        <div>Create Umrah Packages</div>
        <div className="u-btn" onClick={() => onMode('list')}>List Umrah</div>
      </div>
      <Field
        name="Title"
        placeholder="Example: Umrah Maghfiroh"
        value={isTitle}
        onChange={onChangeTitle}
      />
      <TopImage
        image={isThumbnail}
        onChange={addThumbnail}
      />
      <FieldAdd
        type="date"
        name="Umrah Date"
        placeholder="Example: 7"
        data={isTripDates}
        duration={isDuration}
        onChangeText={onChangeDuration}
        onClick={() => addTripDates()}
        onDel={delTripDates}
        onChange={onChangeDate}
      />
      <Field
        name="Cut Off Time"
        placeholder="Example: 3"
        type="number"
        value={isCoT}
        onChange={onChangeCoT}
      />
      <Field
        name="Quota Person"
        placeholder="Example: 50"
        type="number"
        value={isQuota}
        onChange={onChangeQuota}
      />
      <Field
        name="Minimum Participants"
        placeholder="Example: 1"
        type="number"
        value={isMinPax}
        onChange={onChangeMinPax}
      />
      <Field
        type="price"
        name="Price Config"
        value={isPriceConfig}
        onChange={onChangePrice}
      />
      <Field
        type="addon"
        name="Add On"
        value={isAddOn}
        onChange={onChangeAddOn}
      />
      <FieldAdd
        type="visited"
        name="Visited Place"
        data={isVisited}
        onClick={() => addVisited()}
        onDel={delVisited}
        onChange={onChangeVisited}
      />
      <DocPicture
        data={isMedia}
        onChange={onChangeMedia}
      />
      <FieldTextEditor
        name="Itinerary"
        data={isItinerary}
        onClick={() => addItinerary(isItinerary[isItinerary.length - 1].day)}
        onDel={delItinerary}
        onChange={onChangeItinerary}
      />
      <FieldAdd
        type="include"
        name="Include"
        data={isInclude}
        onClick={() => addInclude()}
        onDel={delInclude}
        onChange={onChangeInclude}
      />
      <FieldAdd
        name="Exclude"
        data={isExclude}
        onClick={() => addExclude()}
        onDel={delExclude}
        onChange={onChangeExclude}
      />
      <FieldTextEditor
        name="Terms & Conditions"
        data={isTerms}
        onChange={onChangeTerms}
      />
      <FieldTextEditor
        name="Cancelation Policy"
        data={isPolicy}
        onChange={onChangePolicy}
      />
      <div className="u-publish">
        {isLoading ?
          <div className="u-publish-btn" style={{ paddingRight: 115, paddingLeft: 115 }}>
            <Loading
              loading={isLoading}
              color="#fff"
              type="11"
            />
          </div>
          :
          <div className="u-publish-btn" onClick={() => onSubmit()}>
            PUBLISH
        </div>
        }
      </div>
    </div>
  )
}
