import React from "react";
import { Row, Button, Modal, ModalHeader, ModalBody, Input, Col } from "reactstrap";
import SubHeader from '../../../component/sub-header'
import moment from "moment"
import PaginationRemote from './../../../component/PaginationRemote'
import Dropdown from '../../../component/dropdown'

export default class Traveler extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      params:{
        q:'',
        page:1,
        limit:20,
        sizePerPage: 20,
        totalSize:200
      },
      mobile: false,
      modalSubAgent: false,
      modal: false,
      group_role_id:  "",
      selectSubAgent:null,
      agentCheck: false,
      subAgentCheck: false,
      role_data: [{'id': '1', 'name': 'kitri3110@gmail.com'}, {'id': '2', 'name': 'wawan@gmail.com'},  {'id': '3', 'name': 'bayu@gmail.com'},  {'id': '4', 'name': 'revon@gmail.com'}],
    };
    this.toggleSubAgent = this.toggleSubAgent.bind(this)
    this.toggleClose = this.toggleClose.bind(this)
    this.handleRole = this.handleRole.bind(this)
  }

  componentWillMount(){
    this.hendleResetCustomersAction();
    const { q, page, limit, limitPage } = this.state.params;
    this.loadDataListCustomers(q, page, limit, limitPage)
    if (window.screen.availWidth < 756){
      this.setState({mobile:true})
    } else {
      this.setState({mobile:false})
    }
  }

  hendleResetCustomersAction(){
    const { dispatch } = this.props;
    const { resetCustomersAction } = this.props.actions;
    resetCustomersAction(dispatch)
  }
  loadDataListCustomers(query, page, limit){
    const { dispatch } = this.props;
    let params = {
      q: query,
      page: page,
      limit: limit
    }
    const { customerAction } = this.props.actions;
    // let token = !this.props.login ? '' : this.login.token;
    customerAction(dispatch, params)
  }

  handleInputSearch(index) {
    const { data } = this.state;
    const { setSearching } = this.props.actionsCommon;
    let dataPost = {
      products: data[index].booking
    };
    setSearching(dataPost);
  }
  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page = page;
    params.sizePerPage = sizePerPage;
    this.loadDataListCustomers(params.q, params.page, params.limit);
    this.setState({params:params})
  }
  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page=page;
    params.sizePerPage=sizePerPage;
    this.loadDataListCustomers(params.q, params.page, params.limit);
    this.setState({params:params});
  }
  handleChangeSearch(value){
    let { params } = this.state;
    params.q=value;
    this.setState({params});
    this.loadDataListCustomers(params.q, params.page, params.limit);
  }

  handleAgent(row, index) {

    console.log('AGENT');
    console.log(row);
    // console.log(row.email, row.index);
  }
  handleSubAgent(evt){
    let { selectSubAgent } = this.state;
    selectSubAgent[evt.target.email]=evt.target.value;
    this.setState({ [evt.target.email]: evt.target.value, selectSubAgent})
  }
  submitAgent() { 
    const { payload } = this.props;
    const { actionGetAgent } = this.props.actions;
    actionGetAgent(payload) 
    console.log(actionGetAgent, 'get agent')
  }

  submitSubAgent() { alert('Sub Agent')}
  // choseeUser(row, data, email) { alert('PILIH ', console.log(JSON.stringify(row)))}
  toggleSubAgent(row) {this.setState({ 
    modalSubAgent: !this.state.modalSubAgent, 
    selectSubAgent: row.email}, console.log('Select Email untuk Sub Agent', row.email))
  }
  toggleClose() { this.setState({ modal: !this.state.modal });}
  handleRole(e) { this.setState({group_role_id: e.target.value}) }
  actionAgent(index, row) {
    return (
      <div style={{textAlign:'center', width:65}}><input type="checkbox" onChange={()=>this.handleAgent(row, index)} name="agent" value="Agent"></input></div>
    )
  }
  actionSubAgent(index, row) {
    return (
      <div style={{textAlign:'center', width:65}}><input type="checkbox" onChange={()=>this.toggleSubAgent(row, index)} name="sub-agent" value="SubAgent"></input></div> 
    )
  }
  ckChange = () => {
    alert('aaa')
    // var ckName = document.getElementsByName(el.name);
    // if (el.checked) {
    //   for (var i = 0; i < ckName.length; i++) {
    //     ckName[i].disabled = ckName[i] !== el;
    //   }
    // } else {
    //   for (var i = 0; i < ckName.length; i++) {
    //     ckName[i].disabled = false
    //   }
    // }
  }

  index=0
  indexList=0;
  columns = [
    { dataField: "index", text: "NO", headerStyle: { width: "5%" } },
    { dataField: "fullname", text: "NAME", headerStyle: { width: "22%" } },
    { dataField: "mobileNo", text: "PHONE NUMBER", headerStyle: { width: "17%" } },
    { dataField: "email", text: "EMAIL", headerStyle: { width: "18%" } },
    // { dataField: "birthDate", text: "BIRTH DATE", headerStyle: { width: "10%" } },
    { dataField: "createdAt", text: "DATE CREATE", headerStyle: { width: "17%" }, formatter:(id, row)=>moment(row.date).format("YYYY-MM-DD")},
    { dataField: "status", text: "STATUS", headerStyle: { width: "8%" }, formatter: (id,row)=> <div>USER</div> },
    { dataField: "agent", text: "AGENT", headerStyle: { width: "5%" }, formatter:(index, row) => this.actionAgent(index,row)},
    { dataField: "sub-agent", text: "SUB", headerStyle: { width: "10%" }, formatter:(index, row) => this.actionSubAgent(index,row)}
  ];
  reloadDataUser = false;

  render() {
    const { params,  mobile, selectSubAgent }  = this.state;
    const { meta= {total: 1}  } = this.props.customers;
    const { fetchingListCustomers } = this.props.customers;
    let index=0;
    let no=0
    let dataListCustomers =  this.props.customers.dataListAllCustomers==null ? [] : this.props.customers.dataListAllCustomers;
    let dataViewListCustomers = this.props.customers.dataListCustomers == null ? [] : this.props.customers.dataListCustomers;
    dataViewListCustomers.map((obj,i)=>{
      index++;
      obj.index= params.page===1 ? index : index+Number(params.page*params.sizePerPage)-20;
    });
    dataListCustomers.map((obj,i) => {
      no++;
      obj.index=  no;
    })
    return(
      <div lassName="section-search">
        <Row className="search-flight-heading"  style={{ paddingBottom: "40px" }}>Data Traveler</Row>
        <Row className="step-form-table mbot30">
          <div style={{ width: "100%"}}>
            <SubHeader title={"Data Traveler"} />
            <div className="form-search" style={mobile ? {overflow:"scroll",width:window.innerWidth-20} : {}}>
              <PaginationRemote tableCustomers={true}
                loading = {fetchingListCustomers}
                searchPlaceholder="Search in Here"
                handleChangeSearch={this.handleChangeSearch.bind(this)}
                searchValue={params.q}
                // dataexport={dataListCustomers}
                data={ dataListCustomers }
                columns={this.columns}
                page={ params.pageOptions }
                sizePerPage={ params.sizePerPage }
                totalSize={ !meta.total ? 1 : meta.total }
                onTableChange={ this.handleTableChange }
                onClickAgent={this.submitAgent}
                onClickSubAgent={() => this.toggleSubAgent()}
              />
              <Modal style={{paddingTop:70}} isOpen={this.state.modalSubAgent} toggleClose={this.toggleSubAgent} className={"modal-primary " + this.props.className} >
                <ModalHeader style={{ background: '#008c9a', color: "#ffffff", fontWeight:600 }} toggle={this.toggleSubAgent}>
                  Add Sub Agent
                </ModalHeader>
                <ModalBody style={{ marginTop: 35, marginBottom: 35, paddingTop: 0, paddingBottom: 0 }}>
                  <Row>
                    <Col className="show-tab" size={10}>
                      <Dropdown label={"Agent"} id="group_role_id " data={this.state.role_data} onChange={this.handleRole}/>
                      <Input type="text" id="email" placeholder="Email Sub Agent" value={selectSubAgent} disabled/>
                    </Col>
                  </Row>
                  <Row style={{textAlign:'start', marginTop: 20}}>
                    <Col xs="3" style={{textAlign:'start'}}><Button onClick={this.toggleSubAgent} style={{backgroundColor:'#bebebe', borderColor:'#bebebe'}}><b>Cancel</b></Button></Col>
                    <Col xs="9"><Button onClick={this.submitSubAgent} style={{backgroundColor:'#ea6520', borderColor:'#ea6520'}}><b>Submit</b></Button></Col>
                  </Row>
                </ModalBody>
              </Modal>
            </div>
          </div>
        </Row>
      </div>
    )
  }
}
