import { API_ALL_CONTACT,
  API_URL_DASHBOARD, GET_REPORT, PUT_PASSWORD, GET_LIST_BANNER,
  UPLOAD_IMAGE_BANNER, API_URL, POST_LOGIN, GET_GROUP_ROLE,
  GET_USER, GET_LIST_FLIGHT, POST_REGISTER, UPLOAD_IMAGE,
  GET_DATA_DASHBOARD, GET_CUSTOMERS, POST_AGENTSUB, GET_LIST_ORDER, PUT_AGENTSUB, GET_WITHDRAWAL, POST_REJECT, POST_APPROVE, GET_TOP_UP, POST_REFUND, API_MASTER, CODE_BANK, API_TOUR, TOUR, POST_STATUS_TOUR, STATUS
} from "../config/constant";


export function servicesSearchFlight(
  origin,
  destination,
  departureDate,
  returnDate,
  adults,
  child,
  infant
) {
  let Body = JSON.stringify({
    origin: origin,
    destination: destination,
    departureDate: departureDate,
    adults: adults,
    children: child,
    infants: infant
  });
  if (returnDate !== "") {
    Body = JSON.stringify({
      origin: origin,
      destination: destination,
      departureDate: departureDate,
      returnDate: returnDate,
      adults: adults,
      children: child,
      infants: infant
    });
  }
  const headers = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json"
    },
    body: Body
  };

  return fetch(API_URL + "flight/search", headers).then(handleResponse);
}
function handleResponse(response) {
  return response.text().then(text => {
    const data = text && JSON.parse(text);
    if (!response.ok) {
      // if (response.status === 401) {
      //     // auto logout if 401 response returned from api
      //     logout();
      //     location.reload(true);
      // }
      // const error = (data && data.message) || response.statusText;
      // return Promise.reject(error);
    }
    return data;
  });
}

//5. LOGIN
export function servicesLogin(data) {
  const headers = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json"
    },
    body: JSON.stringify(data)
  };

  return fetch(API_URL + POST_LOGIN, headers).then(handleResponse);
}

//6. GET USERS
export function servicesUser(params) {
  const headers = {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    }
  }
  return fetch(API_ALL_CONTACT, headers).then(handleResponse);
}

//4. REGISTER
export function servicesRegister(data) {
  const headers = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
    body: JSON.stringify(data)
  };

  return fetch(API_ALL_CONTACT, headers).then(handleResponse);
}


//14. UPDATE USER
export function editUser(id, data) {
  const headers = {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      'x-platform-source': 'CMS_ASITA',
      'x-sai-source': 'ASITAAJA',
    },
    body: JSON.stringify(data)
  }
  return fetch(API_URL + GET_USER + '/' + id, headers).then(handleResponse);
}

//16. DELETE USER CMS
export function deleteUser(id, token) {
  const headers = {
    method: "DELETE",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      Authorization: `Bearer ${token}`,
      'x-platform-source': 'CMS_ASITA',
      'x-sai-source': 'ASITAAJA',
    }
  }
  return fetch(API_URL + GET_USER + '/' + id, headers).then(handleResponse);
}

//19. GET LIST BANNER
export function servicesListBanner(token) {
  const headers = {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
      "Accept": "application/json",
      'x-platform-source': 'CMS_ASITA',
      'x-sai-source': 'ASITAAJA',
      "Authorization": `Bearer ${token}`
    }
  };

  return fetch(API_URL + GET_LIST_BANNER, headers).then(handleResponse);
}


//ADMIN LIST TOUR
export function servicePackageTours(params) {
  const headers = {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      "X-Platform-Source": "WEB",
      "X-SAI-Source": "ASITAAJA",
      apiKey: "4OGG3-U8RDM-29NXL-KYE6S-MWW15"
    }
  };
  return fetch(API_TOUR + `admin/authenticated/tours?&apiKey=${params.apiKey}&filters=${params.filters}&pageIndex=${params.pageIndex}&itemsPerPage=${params.itemsPerPage}`, headers).then(handleResponse)
}

export function serviceStatusTour(id, data) {
  const headers = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      "X-Platform-Source": "CMS_ASITA",
      'X-SAI-Source': 'ASITAAJA',
    },
    body: JSON.stringify(data)
  };
  return fetch(API_URL + TOUR + "/" + id + "/" + STATUS, headers).then(handleResponse);
}

// UMRAH
export function createUmrah(payload, token) {
  const headers = {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      "X-Platform-Source": "WEB",
      "X-SAI-Source": "ASITAAJA",
      Authorization: `${token}`,
      apiKey: "4OGG3-U8RDM-29NXL-KYE6S-MWW15"
    },
    body: JSON.stringify(payload)
  };
  return fetch(API_TOUR + `admin/authenticated/umrahs`, headers).then(handleResponse)
}
export function editUmrah(payload, token, id) {
  const headers = {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      "X-Platform-Source": "WEB",
      "X-SAI-Source": "ASITAAJA",
      Authorization: `${token}`,
      apiKey: "4OGG3-U8RDM-29NXL-KYE6S-MWW15"
    },
    body: JSON.stringify(payload)
  };
  return fetch(API_TOUR + `admin/authenticated/umrahs/${id}`, headers).then(handleResponse)
}
export function listUmrah(id) {
  const headers = {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      "X-Platform-Source": "WEB",
      "X-SAI-Source": "ASITAAJA",
    }
  };
  return fetch(API_TOUR + `umrahs/${id}`, headers).then(handleResponse)
}
export function deleteUmrah(token, id) {
  const headers = {
    method: "DELETE",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
      "X-Platform-Source": "WEB",
      "X-SAI-Source": "ASITAAJA",
      Authorization: `${token}`,
      apiKey: "4OGG3-U8RDM-29NXL-KYE6S-MWW15"
    }
  };
  return fetch(API_TOUR + `/admin/authenticated/umrahs/${id}`, headers).then(handleResponse)
}
