import React from "react";
import { Button, Form, FormGroup, Label, Input, Alert, Row } from "reactstrap";
import { SECOND_COLOR } from "../../config/constant"
import { PulseLoader } from "react-spinners";

class Setting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stepName: "Setting",
      oldPassword: "",
      newPassword: "",
      confirmPassword: "",
      errorMassage: "",
      fieldErrorValidationPassword: [],
      errorConfirmPassword: false,
      errorConfirmMsg: "",
      errorConfirNewPass: false,
      errorConfirmPasswordMsg: ""
    };
    this.handleOldPassword = this.handleOldPassword.bind(this);
    this.handleNewPassword = this.handleNewPassword.bind(this);
    this.handleConfrimPassword = this.handleConfrimPassword.bind(this);
  }

  handleOldPassword(e) {
    this.setState({ oldPassword: e.target.value });
  }
  handleNewPassword(e) {
    this.setState({ newPassword: e.target.value });
  }
  handleConfrimPassword(e) {
    this.setState({ confirmPassword: e.target.value });
  }

  changePassword = () => {
    let oldPassword = this.state.oldPassword;
    let newPassword = this.state.newPassword;
    let confirmPassword = this.state.confirmPassword;
    const { dispatch, actions } = this.props
    const { changePasswordAction } = actions

    if (oldPassword === "" || newPassword === "" || confirmPassword === "") {
      this.setState({ errorMassage: "Tolong Isi Semua Data Dengan Benar" });
    } else if (oldPassword === newPassword) {
      // alert("Password Lama & Password Baru Tidak Boleh Sama");
      this.setState({
        errorConfirmPassword: true,
        errorConfirmMsg: "Password Lama & Password Baru Tidak Boleh Sama"
      });
    } else if (oldPassword !== newPassword) {
      // alert("Password Lama & Password Baru Tidak Boleh Sama");
      this.setState({ errorConfirmPassword: true, errorConfirmMsg: "" });
    }
    if (newPassword !== confirmPassword) {
      this.setState({
        errorConfirNewPass: true,
        errorConfirmPasswordMsg:
          "Password Baru & Konfirmasi Password Tidak Sama"
      });
    } else if (newPassword === confirmPassword) {
      this.setState({ errorConfirNewPass: true, errorConfirmPasswordMsg: "" });
    }

    let payload = {
      old_password : this.state.oldPassword,
      new_password : this.state.newPassword
    }

    changePasswordAction(dispatch, this.props.global.user.id, payload, this.handleCallback.bind(this))
  };

  handleCallback(callback){
    alert(callback.message)
  }

  renderSubmit(){
    const {oldPassword, newPassword, confirmPassword} = this.state
    if (oldPassword === "" || newPassword === "" || confirmPassword === "") {
      return (
        <Button type="button" className="button-disable" disabled>
          <b style={{color:'white'}}>SUBMIT</b>
        </Button>
      )
    } else {
      return(
        <Button type="button" onClick={() => this.changePassword()} className="button-register">
          <b style={{color:'white'}}>SUBMIT</b>
        </Button>
      )
    }
  }

  render() {
    const field = this.state.errorConfirmPassword;
    return (
      <div className="section-search">
        <Row className="search-flight-heading">Setting</Row>
        <Row className="step-form">
          <div style={{ width: "100%" }}>
            <div className="form-heading-step"> {this.state.stepName}</div>
            <div className="form-search cc">
              <Form style={{ paddingBottom: 10 }}>
                <FormGroup>
                  <Label className="SignupLabel" for="examplePassword">Old Password </Label>
                  <Input type="password" value={this.state.oldPassword} onChange={this.handleOldPassword} />
                </FormGroup>
                <FormGroup>
                  <Label className="SignupLabel" for="examplePassword">New Password</Label>
                  <Input type="password" value={this.state.newPassword} onChange={this.handleNewPassword}/>
                  <div className={field ? "showErrorMessage" : "hideErrorMessage"} style={{ fontWeight: "bold", color: "red", fontSize: 10 }}>
                    {this.state.errorConfirmMsg}
                  </div>
                </FormGroup>
                <FormGroup>
                  <Label className="SignupLabel" for="examplePassword">Confirm Password</Label>
                  <Input type="password" value={this.state.confirmPassword} onChange={this.handleConfrimPassword} />
                  <div className={field ? "showErrorMessage" : "hideErrorMessage"}style={{ fontWeight: "bold", color: "red", fontSize: 10 }} >
                    {this.state.errorConfirmPasswordMsg}
                  </div>
                </FormGroup>
                  {this.state.errorMassage !== "" ? 
                    ( <Alert color="danger">{this.state.errorMassage}</Alert> ) : ( "" )
                  }
                <FormGroup style={{marginTop:'3vw'}}>
                  {this.renderSubmit()}
                </FormGroup>
              </Form>
            </div>
          </div>
        </Row>
      </div>
    );
  }
}

export default Setting;
