import React from 'react';
import { Editor } from '@tinymce/tinymce-react';
import { bin } from './data'

export default (props) => {
  const { name, onChange, data, onClick, onDel, disable } = props;

  return (
    <div className="u-box">
      <div className="u-row space-between">
        <div style={{ width: '100%' }}>
          <div className="u-subtitle">{name}</div>
        </div>
        {name !== 'Itinerary' ? [] : <div className="u-subtitle button" onClick={onClick}>Add</div>}
      </div>
      {name !== 'Itinerary' ?
        <Editor
          disabled={false}
          apiKey='53tr7u9i12597yy1gmhzi58l2952pi1vmdwsuk426d84smrh'
          initialValue={`<p>${data}</p>`}
          init={{
            width: '100%',
            placeholder: "Type here...",
            height: 300,
            menubar: false,
            plugins: [
              'advlist autolink lists link image charmap print preview anchor',
              'searchreplace visualblocks code fullscreen',
              'insertdatetime media table paste code help wordcount'
            ],
            toolbar:
              'undo redo | formatselect | bold italic backcolor | \
              alignleft aligncenter alignright alignjustify | \
              bullist numlist outdent indent | removeformat | help'
          }}
          onEditorChange={disable ? () => { } : (e) => onChange(e)}
        />
        : data && data.map((item, i) => {
          return (
            <div key={i} style={i === 0 ? {} : { marginTop: 25 }}>
              <div className="u-row">
                <div className="u-title-itinerary">Day #{i + 1} -&nbsp;</div>
                <input
                  type="text"
                  className="u-inputtext title-itinerary"
                  placeholder="Example: Madinah - Makkah"
                  value={item.title}
                  onChange={disable ? () => { } : (e) => onChange(e.target.value, i, 'title')}
                />
              </div>
              <div className="u-row">
                <Editor
                  disabled={false}
                  apiKey='53tr7u9i12597yy1gmhzi58l2952pi1vmdwsuk426d84smrh'
                  initialValue={`<p>${item.description}</p>`}
                  init={{
                    width: '100%',
                    placeholder: "Type here...",
                    height: 300,
                    menubar: false,
                    plugins: [
                      'advlist autolink lists link image charmap print preview anchor',
                      'searchreplace visualblocks code fullscreen',
                      'insertdatetime media table paste code help wordcount'
                    ],
                    toolbar:
                      'undo redo | formatselect | bold italic backcolor | \
                  alignleft aligncenter alignright alignjustify | \
                  bullist numlist outdent indent | removeformat | help'
                  }}
                  onEditorChange={disable ? () => { } : (e) => onChange(e, i, 'description')}
                />
                <img style={{ marginLeft: 30, cursor: 'pointer' }} width="20px" src={bin} onClick={disable ? () => { } : () => onDel(i)} />
              </div>
            </div>
          )
        })}
    </div>
  )
}
