import React from "react";
import { Row, Col, Container } from "reactstrap";

export default class extends React.Component {
  render() {
    const { selectDetail } = this.props;
    const { travelers } = selectDetail
    return (
      <Container
        style={{
          backgroundColor: "#f6f6f6",
          paddingTop: 25,
          paddingBottom: 25
        }}
      >
        <Col style={{ marginLeft: 5, marginRight: 5 }}>
          {!travelers ? [] : travelers.map((item, i) => {
            return (
              <div key={i}>
                <Row>
                  <Col>
                    <b>{item.cabin_type}</b>
                  </Col>
                </Row>
                <Row style={{ marginBottom: 5 }}>
                  <Col size={3}>Name</Col>
                  <Col size={5}>: {`${!item.salutation ? '' : item.salutation + '.'} ${item.first_name}  ${!item.last_name ? '' : item.last_name} `}</Col>
                </Row>
                <Row style={{ marginBottom: 5 }}>
                  <Col size={3}>Country Name</Col>
                  <Col size={5}>: {`${!item.country_name ? '' : item.country_name}`}</Col>
                </Row>
              </div>
            )
          })
          }
        </Col>
      </Container>
    );
  }
}
