import { DO_UPLOAD_IMAGE , UPLOAD_IMAGE_SUCCESS, UPLOAD_IMAGE_FAILED, DO_LIST_BANNER, LIST_BANNER_SUCCESS, LIST_BANNER_FAILED, DO_DELETE_BANNER, DELETE_BANNER_SUCCESS, DELETE_USER_FAILED } from './../../config/constant'

const INITIAL_STATE = {
  fetchingUploadImage: false,
  fetchingListBanner:false,
  fetchingDeleteBanner:false,
  dataUploadImage: null,
  dataListBanner:null,
  responseDeleteBanner:null
}

export default function dataReducer(state = INITIAL_STATE, action) {
  switch(action.type) {

    // START BANNER
    case DO_UPLOAD_IMAGE : {
      return {
        ...state,
        fetchingUploadImage: true
      }
    }
    case UPLOAD_IMAGE_SUCCESS: {
      return {
        ...state,
        fetchingUploadImage: false,
        dataUploadImage: action.data
      }
    }
    case UPLOAD_IMAGE_FAILED: {
      return {
        ...state,
        fetchingUploadImage: false
      }
    }
    // LIST BANNER
     case DO_LIST_BANNER : {
      return {
        ...state,
        fetchingListBanner: true
      }
    }
    case LIST_BANNER_SUCCESS: {
      return {
        ...state,
        fetchingListBanner: false,
        dataListBanner: action.data
      }
    }
    case LIST_BANNER_FAILED: {
      return {
        ...state,
        fetchingListBanner: false
      }
    }
    // DELETE BANNER
    case DO_DELETE_BANNER : {
      return {
        ...state,
        fetchingDeleteBanner: true
      }
    }
    case DELETE_BANNER_SUCCESS: {
      return {
        ...state,
        fetchingDeleteBanner: true,
        responseDeleteBanner: action.data
      }
    }
    case DELETE_USER_FAILED: {
      return {
        ...state,
        fetchingDeleteBanner: false
      }
    }
    default:
    return state
  }
}
