import { DO_CHANGE_PASSWORD, CHANGE_PASSWORD_SUCCESS, CHANGE_PASSWORD_FAILED } from './../../config/constant'

const INITIAL_STATE = {
  fetchingPassword: false,
  dataPassword: null,
  meta: null
}

export default function dataReducer(state = INITIAL_STATE, action) {
  let { dataPassword } = state;
  switch(action.type) {
    case DO_CHANGE_PASSWORD : {
      return {
        ...state,
        fetchingPassword: true,
      }
    }
    case CHANGE_PASSWORD_SUCCESS: {
      dataPassword = action.data
      return {
        ...state,
        fetchingPassword: false,
        dataPassword
      }
    }
    case CHANGE_PASSWORD_FAILED: {
      dataPassword = null
      return {
        ...state,
        fetchingPassword: false,
        dataPassword
      }
    }
    default:
    return state
  }
}
