import React from "react";
import { Row } from "reactstrap";
import { Bar, Pie } from "react-chartjs-2";
import Loading from '../../../component/Loading/index';
import 'chartjs-plugin-datalabels';
import { formatRupiah } from './../../../utilities/helpers/index';

const mainChartOpts = {
  maintainAspectRatio: false,
  tooltips: {
    enabled: true,
    callbacks: {
      label: (tooltipItem) => {
        return formatRupiah(tooltipItem.yLabel);
      }
    }
  },
  plugins: {
    datalabels: {
      textStrokeColor:'#23134d',
      textStrokeWidth:2,
      formatter: function(value) {
        return  formatRupiah(value);
      },
      color: function(context){
        return 'white';
      },
      align:'top',
      padding:{top:12},
      font: {
        weight: "500"
      }
    }
  },
  legend: {
    display: false
  },
  scales: {
    xAxes: [
      {
        gridLines: {
          drawOnChartArea: false,
        },
        ticks: {
          fontSize: 13
        }
      }
    ],
    yAxes: [
      {
        ticks: {
          beginAtZero: true,
          callback: function(value, index, values) {
              return formatRupiah(value);
          },
          fontSize: 13
        }
      }
    ]
  },
  Bar:  [5],
  elements: {
    line: {
      borderWidth: 1
    }
  }
};

const Data = {
  datasets: [
    { 
      data: ["300", "100"],
      backgroundColor: ["#a3195b", "#0290d2"]
    }
  ],
    labels: ["Garuda", "Sriwijaya"]
  };

export default class DiagramTotal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  renderLoading(){
    return(
      <div className="box-loading" style={{marginTop: 50, marginBottom:50}}>
        <Loading
          type="PulseLoader"
          loading={true}
          color={'#0290d2'}
          size={25}
        />
      </div>
    )
  }

  render() {
    const { totalSales, dataTotalSales, mountReport, loading } = this.props;
    if (loading) return this.renderLoading();
    return (
      <div style={{ marginTop: 50, marginBottom: 25, width:'100%' }}>
        {totalSales ? (
          <div className="box-container-pie">
            <Row style={{ width: 800, textAlign: "center", marginLeft: 200 }}>
              <Pie data={Data} />
            </Row>
          </div>
        ) : (
          <Row style={{marginRight: 15, marginLeft: 15}}>
            <Bar data={mountReport} options={mainChartOpts} height={490} />
          </Row>
        )}
      </div>
    );
  }
}
