import {
  DO_LIST_CUSTOMERS, LIST_CUSTOMERS_SUCCESS, LIST_CUSTOMERS_FAILED, RESET_CUSTOMERS_SUCCESS,
  DO_LOAD_ALL_CUSTOMERS, LOAD_ALL_CUSTOMERS_SUCCESS, LOAD_ALL_CUSTOMERS_FAILED, DO_CREATE_AGENTSUB, CREATE_AGENTSUB_SUCCESS, CREATE_AGENTSUB_FAILED, RESET_AGENTSUB_SUCCESS,

} from './../../../config/constant'

const INTIAL_STATE = {
  fetchingListCustomers: false,
  fetchingListLoading: false,
  fetchingAllDataCustomers: false,
  fetchingDataAgentSub: false,
  refreshDataCus: null,
  dataListAllCustomers: [],
  dataAgentSub: null,
  meta: null,
  err: null
}

export default function dataCustomers(state = INTIAL_STATE, action) {
  switch (action.type) {
    case DO_LIST_CUSTOMERS: {
      return {
        fetchingListCustomers: true,
        fetchingListLoading: true
      }
    }
    case LIST_CUSTOMERS_SUCCESS: {
      return {
        ...state,
        fetchingListCustomers: false,
        fetchingListLoading: false,
        dataListAllCustomers: [...action.data],
        refreshDataCus: action.data,
        meta: action.meta
      }
    }
    case LIST_CUSTOMERS_FAILED: {
      return {
        ...state,
        fetchingListCustomers: false,
        fetchingListLoading: false
      }
    }
    case RESET_CUSTOMERS_SUCCESS: {
      return {
        ...state,
        fetchingListCustomers: false,
        fetchingListLoading: false,
        dataListCustomers: []
      }
    }
    //DATA CUSTOMER
    case DO_LOAD_ALL_CUSTOMERS: {
      return {
        ...state,
        fetchingAllDataCustomers: true
      }
    }
    case LOAD_ALL_CUSTOMERS_SUCCESS: {
      return {
        ...state,
        fetchingAllDataCustomers: false,
        dataListCustomers: action.data,
        meta: action.meta
      }
    }
    case LOAD_ALL_CUSTOMERS_FAILED: {
      return {
        ...state,
        fetchingAllDataCustomers: false
      }
    }
    //DATA AGENT
    case DO_CREATE_AGENTSUB: {
      return {
        ...state,
        fetchingDataAgentSub: true,
      }
    }
    case CREATE_AGENTSUB_SUCCESS: {
      return {
        ...state,
        fetchingDataAgentSub: false,
        dataAgentSub: action.data,
        meta: action.meta
      }
    }
    case CREATE_AGENTSUB_FAILED: {
      return {
        ...state,
        fetchingDataAgentSub: false
      }
    }
    case RESET_AGENTSUB_SUCCESS: {
      return {
        ...state,
        fetchingDataAgentSub: false,
        dataAgentSub: []
      }
    }
    default:
      return state
  }
}
