import React from 'react';
import Loading from '../../component/Loading/index';
import { Button, Col } from "reactstrap";


export default class Component extends React.Component {
  render() {
    const { label, handleSubmit, disabled, loadingColor='#fff', typeLoading="PulseLoader", loading=false, loadingSize=14  } = this.props;
    return(
      <Col size={3}>
         <p style={{ marginTop: 36 }} />
         {loading ? 
            <div 
              className="button-primary"
              style={{ alignItems: "right", width: 250, height: 40, borderRadius:5,
                display:'flex', 
                flexDirection:'row',
                alignItems:'center',
                justifyContent:'center'

              }}
            >
              <Loading
                type={typeLoading}
                loading={loading}
                color={loadingColor}
                size={loadingSize}
              />
            </div>
            :
            <Button
              disabled={disabled}
              onClick={()=>handleSubmit()}
              className="button-primary"
              style={{ alignItems: "right", width: 250, height: 40 }}
            >
            <b>{label}</b>
          </Button>
        }
    </Col>
    )
  }
}
