import {
  CREATE_UMRAH,
  CREATE_UMRAH_FAILED,
  CREATE_UMRAH_SUCCESS,
  LIST_UMRAH,
  LIST_UMRAH_SUCCESS,
  LIST_UMRAH_FAILED,
  DELETE_UMRAH,
  DELETE_UMRAH_FAILED,
  DELETE_UMRAH_SUCCESS,
} from './type';

const initialState = {
  isLoading: false,
  success: true,
  message: '',
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case CREATE_UMRAH:
      return {
        ...state,
        isLoading: true
      };
    case CREATE_UMRAH_SUCCESS:
      return {
        ...state,
        isLoading: false,
        success: action.data,
        message: action.message
      };
    case CREATE_UMRAH_FAILED:
      return {
        ...state,
        isLoading: false
      };
    case LIST_UMRAH:
      return {
        ...state,
        isLoading: true
      };
    case LIST_UMRAH_SUCCESS:
      return {
        ...state,
        isLoading: false,
      };
    case LIST_UMRAH_FAILED:
      return {
        ...state,
        isLoading: false
      };
    case DELETE_UMRAH:
      return {
        ...state,
        isLoading: true
      };
    case DELETE_UMRAH_SUCCESS:
      return {
        ...state,
        isLoading: false,
      };
    case DELETE_UMRAH_FAILED:
      return {
        ...state,
        isLoading: false
      };
    default:
      return state;
  }
}

export default reducer;
