import React from "react";
import { Row, Col } from "reactstrap";
import SubHeader from "../../component/sub-header";
import { PRIMARY_COLOR } from "../../config/constant";
import PaginationRemote from '../../component/PaginationRemote'
import { formatRupiah } from '../../utilities/helpers';
import moment from "moment";


export default class TopUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      params: {
        q: '',
        page: 1,
        limit: 20,
        sizePerPage: 20,
        totalSize: 200,
        start_date: '',
        end_date: '',
        customer_id: '',
        status: '',
      },
      data: [],
      mobile: false,
      errorMessage: "",
      search: "",
    }
  }

  componentWillMount() {
    this.handleResetTopUp()
    const { q, page, limit, start_date, end_date, customer_id, status } = this.state.params
    this.loadDataListTopUp(q, page, limit, start_date, end_date, customer_id, status)
  }
  handleResetTopUp() {
    const { dispatch } = this.props;
    const { resetTopUpAction } = this.props.actions
    resetTopUpAction(dispatch)
  }
  loadDataListTopUp(query, page, limit, startDate, endDate, idCustomer, status) {
    const { dispatch } = this.props;
    let params = {
      q: query,
      page: page,
      limit: limit,
      start_date: startDate,
      end_date: endDate,
      customer_id: idCustomer,
      status: status,
    }
    const { topUpAction } = this.props.actions;
    let token = !this.props.login.token ? '' : this.props.login.token;
    topUpAction(dispatch, params, token)
  }

  //HANDLE CHANGE TABLE
  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page = page;
    params.sizePerPage = sizePerPage;
    this.loadDataListTopUp(params.q, params.page, params.limit, params.start_date, params.end_date, params.customer_id, params.status)
    this.setState({ params: params });
  }
  //HANDLE SEARCH
  handleChangeSearch(value) {
    let { params } = this.state
    params.q = value
    this.setState({ params });
    this.loadDataListTopUp(params.q, params.page, params.limit, params.start_date, params.end_date, params.customer_id, params.status);
  }
  renderStatusFlight(id, row) {
    return (
      <div className={(!row ? '' : !row.status ? '' : row.status).toLowerCase()}>{!row ? '' : !row.status ? '' : row.status}</div>
    );
  }
  indexList = 0;
  columns = [
    { dataField: "index", text: "NO", headerStyle: { width: "3%" } },
    { dataField: "paid_at", text: "DATE PAID", headerStyle: { width: "25%" }, formatter: (id, row) => moment(row.created_at).format("DD-MM-YYYY") },
    { dataField: "customer_fullname", text: "NAME", headerStyle: { width: "22%" } },
    { dataField: "customer_email", text: "EMAIL", headerStyle: { width: "15%" } },
    { dataField: "amount", text: "AMOUNT", headerStyle: { width: "15%" }, formatter: (id, row) => formatRupiah(!row ? 0 : !row.amount ? 0 : row.amount) },
    { dataField: "code", text: "CODE", headerStyle: { width: "7%" } },
    // { dataField: "customer_id", text: "ID", headerStyle: { width: "7%" }},
    { dataField: "status", text: "STATUS", headerStyle: { width: "5%" }, formatter: this.renderStatusFlight.bind(this) },
  ]
  render() {
    const { params } = this.state;
    const { fetchingListTopUp } = this.props.topup;
    const { meta = { total: 1 } } = this.props.topup;
    let index = 0;
    let viewDataTopUp = this.props.topup.dataTopUp == null ? [] : this.props.topup.dataTopUp;
    viewDataTopUp.map((obj, i) => {
      index++;
      obj.index = params.page == 1 ? index : index + Number(params.page * params.sizePerPage) - 20;
    })
    return (
      <div className="section-search">
        <Row className="search-flight-heading bot">List Data Top-Up</Row>
        <Row className="step-form-table">
          <div style={{ width: "100%" }}>
            <SubHeader title={"List Data Top-Up"} />
            <div className="form-search" >
              <PaginationRemote tableCustomers={true}
                loading={fetchingListTopUp}
                searchPlaceholder="Search in Here"
                handleChangeSearch={this.handleChangeSearch.bind(this)}
                searchValue={params.q}
                // dataexport= { filterData }
                data={viewDataTopUp}
                columns={this.columns}
                page={params.page}
                sizePerPage={params.sizePerPage}
                totalSize={!meta ? 1 : meta.total}
                onTableChange={this.handleTableChange}
              />
            </div>
          </div>
        </Row>
      </div>
    )
  }
}
