import React from 'react';
import DatePicker from "react-datepicker";
import { bin } from './data'
import moment from 'moment';

export default (props) => {
  const { name, onChange, data, onClick, type, duration, onChangeText, placeholder, onDel, disable } = props;
  return (
    <div className="u-box">
      <div className="u-row space-between">
        <div style={{ width: '100%' }}>
          <div className="u-subtitle">{name}</div>
        </div>
        <div className="u-subtitle button" onClick={onClick}>Add</div>
      </div>
      {type === 'date' ?
        <div>
          <div>
            <div className="u-title-subtitle">Duration days</div>
            <input
              type={type === "date" ? "number" : "text"}
              className="u-inputtext"
              placeholder={placeholder}
              value={duration}
              onChange={disable ? () => { } : (e) => onChangeText(e.target.value)}
            />
          </div>
          {data && data.map((item, i) => {
            return (
              <div key={i} className="u-row" style={{ marginTop: 15 }}>
                <div style={{ width: '100%' }}>
                  <div className="u-title-subtitle">Start Date</div>
                  <DatePicker
                    onChange={disable ? () => { } : (date) => onChange('startDate', date, i)}
                    className="u-inputtext u-pd0 black"
                    placeholderText={moment(item.startDate || item.start_date).format('DD-MM-YYYY')}
                    dateFormat="dd-mmm-yyyy"
                  />
                </div>
                <div style={{ width: '100%' }}>
                  <div className="u-title-subtitle">End Date</div>
                  <div className="u-row">
                    <DatePicker
                      onChange={disable ? () => { } : (date) => onChange('endDate', date, i)}
                      className="u-inputtext u-pd0 black"
                      placeholderText={moment(item.endDate || item.end_date).format('DD-MM-YYYY')}
                      dateFormat="dd-mmm-yyyy"
                    />
                    <img style={{ marginLeft: 30, cursor: 'pointer' }} width="20px" src={bin} onClick={disable ? () => { } : () => onDel(i)} />
                  </div>
                </div>
              </div>
            )
          })}
        </div>
        :
        data && data.map((item, i) => {
          return (
            <div key={i} style={i === 0 ? {} : { marginTop: 15 }}>
              <div className="u-title-subtitle">#{i + 1}</div>
              <input
                type="text"
                className="u-inputtext"
                placeholder={type === "visited" ? "Example: Madinah" : type === "include" ? "Example: Hotel" : "Example: Passport"}
                value={type === "visited" ? item.name : item.title || item}
                onChange={disable ? () => { } : (e) => onChange(e.target.value, i)}
              />
              <img style={{ marginLeft: 30, cursor: 'pointer' }} width="20px" src={bin} onClick={disable ? () => { } : () => onDel(i)} />
            </div>
          )
        })
      }
    </div >
  )
}
