import { DO_LIST_REPORT, LIST_REPORT_SUCCESS, LIST_REPORT_FAILED, RESET_REPORT_SUCCESS,
DO_LOAD_ALL_REPORT, LOAD_ALL_REPORT_SUCCESS, LOAD_ALL_REPORT_FAILED} from '../../config/constant'

const INTIAL_STATE = {
  fetchingListReport: false,
  fetchingListLoading: false,
  fetchingAllDataReport:false,
  dataListReport: [],
  dataListAllReport: [],
  meta: null,
  err: null
}

export default function dataReducer(state = INTIAL_STATE, action){
  let { dataListReport, dataListAllReport } = state;
  switch(action.type) {
    case DO_LIST_REPORT : {
      return {
        ...state,
        fetchingListReport: true,
        fetchingListLoading: true,

      }
    }
    case LIST_REPORT_SUCCESS: {
      return {
        ...state,
        fetchingListReport: false,
        fetchingListLoading: false,
        dataListReport: [...action.data],
        meta: action.meta
      }
    }
    case LIST_REPORT_FAILED: {
      return {
        ...state,
        fetchingListReport: false,
        fetchingListLoading: false,

      }
    }
    case RESET_REPORT_SUCCESS: {
      return {
        ...state,
        fetchingListReport: false,
        fetchingListLoading: false,
        dataListReport:[]
      }
    }

    //ALL DATA REPORT
    case DO_LOAD_ALL_REPORT:{
      return {
        ...state,
        fetchingAllDataReport: true,
      }
    }
    case LOAD_ALL_REPORT_SUCCESS: {
      return {
        ...state,
        fetchingAllDataReport: false,
        dataListAllReport: action.data,
        meta: action.meta
      }
    }
    case LOAD_ALL_REPORT_FAILED: {
      return {
        ...state,
        fetchingAllDataReport: false,
      }
    }
    default: 
      return state
  }
}
