import React from "react";
import { Row, Col, Button } from "reactstrap";
import DropdownData from "./component/dropdown";
import DiagramTotal from "./component/diagram-total";
import Tabs from "../../component/tabs";
import moment from "moment";
import './styles.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import SubHeader from '../../component/sub-header';
import Loading from '../../component/Loading/index';


export default class Dashboard extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      titleChart: 'Chart Domestic',
      startDate: moment().add('days', -30).format('YYYY-MM-DD'),
      endDate: moment().add('days').format('YYYY-MM-DD'),
      selectRoute: '',
      selectPriode: new Date(),
      selectAirline: '',
      departureDate: moment(),
      departureDateValue: moment().format('YYYY-MM-DD'),
      endDateValue: "",
      product: '',
      productValue: '',
      selectedDataAirline: 'Flight',
      selectedDataRoute: 'Flight',
      selectFilter: '',
      mobile: false
    };
    this.handleDateStart = this.handleDateStart.bind(this);
    this.handleDateEnd = this.handleDateEnd.bind(this);
  }

  componentWillMount() {
    this.handleLoadDataChart();
    if (window.screen.availWidth < 756) {
      this.setState({ mobile: true })
    } else {
      this.setState({ mobile: false })
    }
  }

  handleDateStart(date) {
    let startDate = moment(date).format('YYYY-MM-DD');
    let endDate = moment(startDate).add(1, 'days');
    let endDateFormat = moment(endDate).format('YYYY-MM-DD');

    this.setState({
      startDate: date,
      startDateValue: startDate,
      endDate: endDate,
      endDateValue: (this.state.isRoundTrip) ? endDateFormat : ""
    });
  }

  handleDateEnd(date) {
    let endDateFormat = moment(date).format('YYYY-MM-DD');
    this.setState({
      endDate: date,
      endDateValue: endDateFormat
    });
  }

  handleSelectDropDown(selectProduct) {
    this.setState({ selectedProduct: selectProduct })
  }

  handleSelectDate(field, value) {
    this.setState({ [field]: moment(value).format('YYYY-MM-DD') });
  }
  callbackSubmit(callback) {
    if (callback.success) {
      this.handleSetTitleSubmit();
    }
  }

  handleSetTitleSubmit() {
    let { titleChart, selectRoute } = this.state;
    if (selectRoute.toLowerCase() == 'international') {
      this.setState({ titleChart: 'Chart International' });
    } else {
      this.setState({ titleChart: 'Chart Domestic' });
    }
  }
  handleLoadDataChart() {
    const { actions, dispatch } = this.props;
    const { startDate, endDate, selectAirline, selectRoute, selectFilter } = this.state;
    let params =
    {
      q: '',
      start_date: startDate,
      end_date: endDate,
      airline: !selectAirline ? '' : selectAirline.toLowerCase(),
      routes: !selectRoute ? 'domestic' : selectRoute.toLowerCase(),
      status: selectFilter
    }
    let token = !this.props.login.token ? '' : this.props.login.token;
    actions.loadDataChartDashboard(dispatch, params, token, this.callbackSubmit.bind(this));
  }
  handleSubmit() {
    this.handleLoadDataChart();
  }
  handleSelectAirline(value) {
    this.setState({ selectAirline: value });
  }
  handleSelectRoute(value) {
    this.setState({ selectRoute: value });
  }
  hedleSelectFilter(value) {
    this.setState({ selectFilter: value })
  }
  renderLoading() {
    return (
      <div className="box-loading" style={{ marginTop: 50, marginBottom: 50 }}>
        <Loading
          type="PulseLoader"
          loading={true}
          color={'#0290d2'}
          size={25}
        />
      </div>
    );
  }

  render() {
    const dataMountSales = {
      datasets: [
        {
          data: [],
          backgroundColor: ["#a3195b", '#0290d2', "#02d002", '#FF9933', '#FF00FF', '#FF0000', '#d48214', '6600FF', ' #00bcd4', '#ff5722', '#182465', '#ffeb3b',]
        }
      ],
      labels: ['', '', '', '', '', '', '', '', '', '', '']
    };

    const { startDate, endDate, selectAirline, selectRoute, titleChart, selectFilter, mobile } = this.state;
    let _dataChartDasboard = this.props.dashboard.dataChartDashboard != null ? this.props.dashboard.dataChartDashboard.content : null;
    const { fetchingDataChartDashboard } = this.props.dashboard;
    if (_dataChartDasboard == null) {
      dataMountSales.datasets[0].data = [];
    } else {
      var dataMerge = { domestic: [], international: null };
      let mergeName = 'GA';
      let mergeTotalTrans = 0;
      let mergeAmountTrans = 0;
      // const zeroArray = new Array(0);
      // let data_dashboard = _dataChartDasboard !== null ? _dataChartDasboard.domestic : null;
      // var i=0
      // for(i=0;i<data_dashboard;i++) {
      //   if (
      //     _dataChartDasboard.domestic[i].airline_name.toUpperCase()=="GA" ||
      //     _dataChartDasboard.domestic[i].airline_name.toUpperCase()=="GARUDA INDONESIA" ||
      //     _dataChartDasboard.domestic[i].airline_name.toUpperCase()== ""
      //   ){
      //     mergeTotalTrans += _dataChartDasboard.domestic[i].total_transaction;
      //     mergeAmountTrans += _dataChartDasboard.domestic[i].amount_transaction;
      //   }else {
      //     dataMerge.domestic.push({
      //       airline_name: _dataChartDasboard.domestic[i].airline_name,
      //       amount_transaction: _dataChartDasboard.domestic[i].amount_transaction,
      //       total_transaction: _dataChartDasboard.domestic[i].total_transaction
      //     });
      //   }
      // }

      if (_dataChartDasboard.domestic != null) {
        for (var i = 0; i < _dataChartDasboard.domestic.length; i++) {
          if (
            _dataChartDasboard.domestic[i].airline_name.toUpperCase() == "GA" ||
            _dataChartDasboard.domestic[i].airline_name.toUpperCase() == "GARUDA INDONESIA" ||
            _dataChartDasboard.domestic[i].airline_name.toUpperCase() == ""
          ) {
            mergeTotalTrans += _dataChartDasboard.domestic[i].total_transaction;
            mergeAmountTrans += _dataChartDasboard.domestic[i].amount_transaction;
          } else {
            dataMerge.domestic.push({
              airline_name: _dataChartDasboard.domestic[i].airline_name,
              amount_transaction: _dataChartDasboard.domestic[i].amount_transaction,
              total_transaction: _dataChartDasboard.domestic[i].total_transaction
            });
          }
        }
      }

      if (dataMerge.domestic != null) {
        dataMerge.domestic.push({
          airline_name: mergeName,
          amount_transaction: mergeAmountTrans,
          total_transaction: mergeAmountTrans
        });
      }
      if (selectRoute.toLowerCase() == 'international') {
        if (dataMerge.international == null) {
          dataMountSales.datasets[0].data = [];
        } else {
          dataMerge.international.map((flight, i) => {
            dataMountSales.datasets[0].data.push(flight.amount_transaction);
            dataMountSales.labels[i] = flight.airline_name;
          });
        }
      } else {
        if (dataMerge.domestic == null) {
          dataMountSales.datasets[0].data = [];
        } else {
          dataMerge.domestic.map((flight, i) => {
            dataMountSales.datasets[0].data.push(flight.amount_transaction);
            dataMountSales.labels[i] = flight.airline_name;
          });
        }
      }
    }
    return (
      <div className="section-search">
        <Row className="search-flight-heading bot"> Dashboard</Row>
        <Row className="step-form-table bot">
          <div style={mobile ? { width: window.innerWidth } : { width: "100%" }}>
            <SubHeader title={"Diagram Reporting"} />
            <Row style={mobile ? { marginTop: 10, marginLeft: 5, marginRight: 5 } : { marginTop: 50, marginLeft: 20, marginRight: 20 }}>
              <Col size={3}>
                <div>Start Date</div>
                <div>
                  <DatePicker
                    selected={new Date(startDate)}
                    onChange={(e) => this.handleSelectDate('startDate', e)}
                    style={{ padding: 20 }}
                  />
                </div>
              </Col>
              <Col size={3}>
                <div>End Date</div>
                <div>
                  <DatePicker
                    selected={new Date(endDate)}
                    onChange={(e) => this.handleSelectDate('endDate', e)}
                    style={{ padding: 20 }}
                  />
                </div>
              </Col>
              <Col size={3}>
                <div>Airline</div>
                <div><DropdownData handleSelect={this.handleSelectAirline.bind(this)} selected={selectAirline} selectAirline={true} /></div>
              </Col>
              {/* <Col size={2}>
                <div style={{marginTop:22}}>
                  <Button className="button-primary" style={{width:'90%',padding:8}}  onClick={()=>this.handleSubmit()}>
                    Submit
                  </Button>
                </div>
              </Col> */}
              {/* <Col size={2}>
                <div style={{marginLeft:2}}>Origin</div>
                <div className="box-input-search">
                  <input value={this.state.searchOrigin} placeholder={"Search Origin"} onChange={(e)=>this.handleChangeSearch(e.target.value)} className="input-search-dashboard"/>
                </div>
              </Col> */}
            </Row>
            <Row style={mobile ? { marginTop: 10, marginLeft: 5, marginRight: 5 } : { marginLeft: 20, marginRight: 20 }}>
              <Col size={3}>
                <div>Route</div>
                <div><DropdownData handleSelect={this.handleSelectRoute.bind(this)} selected={selectRoute} dataRoute={true} /></div>
              </Col>
              <Col size={3}>
                <div style={{ marginBottom: 5 }}>Filter Data</div>
                <div style={mobile ? { marginLeft: 15, marginRight: "10vh" } : { marginLeft: 15 }}><DropdownData handleSelect={this.hedleSelectFilter.bind(this)} selected={selectFilter} filterData={true} /></div>
              </Col>
              <Col size={3}>
                <div style={{ marginTop: 20 }}>
                  <Button className="button-primary" style={{ width: '55%', padding: 8, paddingLeft: 8 }} onClick={() => this.handleSubmit()}>
                    <b>Submit</b>
                  </Button>
                </div>
              </Col>
            </Row>
            <Row>
              <Col size={2} style={mobile ? { marginTop: 40, marginBottom: -20 } : { marginLeft: 20, marginRight: 20 }}><b style={{ fontSize: 18, marginLeft: 10, color: '#222222' }}>{titleChart}</b></Col>
            </Row>
            <Row style={mobile ? { marginLeft: 5, marginRight: 5 } : { marginBottom: 10, marginLeft: 25, marginRight: 25 }}>
              {fetchingDataChartDashboard ?
                this.renderLoading() :
                <DiagramTotal mobile={mobile} mountReport={dataMountSales} totalSales={false} />
              }
            </Row>
          </div>
        </Row>
      </div>
    );
  }
}
