import React from "react";
import { Row, Button, Modal, ModalHeader, ModalBody } from "reactstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import Tabs from "../../component/tabs";
import InfoPassenger from "./component/info-passenger";
import ContactDetail from "./component/contact-details";
import SubHeader from "../../component/sub-header";
import { SECOND_COLOR } from "../../config/constant";

export default class DataHotel extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      modalOrder: false,
      search: "",
      products: [
        {
          id: 1,
          booking: "73367719",
          order: "SH555/GA",
          date: "18-07-2019",
          hotel: "Haris Hotel - Bandung",
          amount: "Rp. 10.000.000",
          status: <div className="yet-paid">YET PAID</div>
        },
        {
          id: 2,
          booking: "63367719",
          order: "JH555/GA",
          date: "18-07-2019",
          hotel: "Airy Rooms - Superior Double Room Only - Semarang",
          amount: "Rp. 10.000.000",
          status: <div className="yet-paid">YET PAID</div>
        },
        {
          id: 3,
          booking: "53367719",
          order: "SH555/GA",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: <div className="yet-paid">YET PAID</div>
        },
        {
          id: 4,
          booking: "43367719",
          order: "SH555/GA",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: (
            <div className="paid" style={{ textAlign: "center" }}>
              PAID
            </div>
          )
        },
        {
          id: 5,
          booking: "33367719",
          order: "LH055/LI",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: <div className="yet-paid">YET PAID</div>
        },
        {
          id: 6,
          booking: "23367719",
          order: "SH555/GA",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: <div className="paid">PAID</div>
        },
        {
          id: 7,
          booking: "13367719",
          order: "KH5325/GA",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: <div className="paid">PAID</div>
        },
        {
          id: 8,
          booking: "83367719",
          order: "SH555/GA",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: <div className="paid">PAID</div>
        },
        {
          id: 9,
          booking: "79367719",
          order: "GG555/GA",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 100.000.000",
          status: <div className="yet-paid">YET PAID</div>
        },
        {
          id: 10,
          booking: "39367719",
          order: "HH555/CTL",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: <div className="paid">PAID</div>
        },
        {
          id: 11,
          booking: "79367719",
          order: "SH555/GA",
          date: "18-07-2019",
          hotel: "Hotel Borobudur - Jakarta",
          amount: "Rp. 10.000.000",
          status: <div className="paid">PAID</div>
        },
        {
          id: 12,
          booking: "70367719",
          order: "SH555/MG",
          date: "18-07-2019",
          hotelrute: "Hotel Borobudur - Jakarta",
          amount: "Rp. 1000.000.000",
          status: <div className="paid">PAID</div>
        }
      ]
    };
    this.toggleOrder = this.toggleOrder.bind(this);
    this.actionCol = this.actionCol.bind(this);
  }
  toggleOrder() {
    this.setState({ modalOrder: !this.state.modalOrder });
  }
  actionCol(id, row) {
    return (
      <div>
        <Button onClick={this.toggleOrder} className="button-primary">
          VIEW
        </Button>
      </div>
    );
  }

  render() {
    const { SearchBar } = Search;
    const columns = [
      { dataField: "id", text: "NO", headerStyle: { width: "5%" } },
      {
        dataField: "booking",
        text: "BOOKING ID",
        headerStyle: { width: "13%" }
      },
      { dataField: "order", text: "ORDER ID", headerStyle: { width: "13%" } },
      { dataField: "date", text: "DATE", headerStyle: { width: "13%" } },
      { dataField: "hotel", text: "HOTEL", headerStyle: { width: "22%" } },
      { dataField: "amount", text: "AMOUNT", headerStyle: { width: "15%" } },
      { dataField: "status", text: "STATUS", headerStyle: { width: "10%" } },
      {
        dataField: "action",
        text: "ACTION",
        headerStyle: { width: "10%" },
        formatter: this.actionCol
      }
    ];
    const pageOptions = {
      paginationSize: 10,
      pageStartIndex: 1,
      withFirstAndLast: false,
      hideSizePerPage: true,
      hidePageListOnlyOnePage: true
    };
    return (
      <div className="section-search">
        <Row
          className="search-flight-heading"
          style={{ paddingBottom: "40px" }}
        >
          Data Hotel
        </Row>
        <Row className="step-form-table">
          <div style={{ width: "100%" }}>
            <SubHeader title={"Data Hotel"} />
            <div className="form-search">
              <ToolkitProvider
                keyField="id"
                data={this.state.products}
                columns={columns}
                search
              >
                {props => (
                  <div style={{ marginTop: 10, marginBottom: 50 }}>
                    <SearchBar
                      className="custome-search-field"
                      placeholder="Search In Here"
                      {...props.searchProps}
                    />
                    <hr />
                    <BootstrapTable
                      pagination={paginationFactory(pageOptions)}
                      striped
                      {...props.baseProps}
                    />
                  </div>
                )}
              </ToolkitProvider>
              <Modal
                isOpen={this.state.modalOrder}
                togglePrimary={this.toggleOrder}
                className={"modal-primary " + this.props.className}
              >
                <ModalHeader
                  style={{ background: SECOND_COLOR, color: "#ffffff" }}
                  toggle={this.toggleOrder}
                >
                  Details Order
                </ModalHeader>
                <ModalBody
                  style={{
                    marginTop: 0,
                    marginBottom: 0,
                    paddingTop: 0,
                    paddingBottom: 0
                  }}
                >
                  <Row className="show-tab">
                    <Tabs>
                      <InfoPassenger title="Pessanger Information" />
                      <ContactDetail title="Contact Details" />
                    </Tabs>
                  </Row>
                </ModalBody>
              </Modal>
            </div>
          </div>
        </Row>
      </div>
    );
  }
}
