import React from "react";
import { Row, Button, Modal, ModalHeader, ModalBody } from "reactstrap";
import Tabs from "../../component/tabs";
import InfoPassenger from "./../../component/FlightListDetail/info-passenger";
import ContactDetail from "./../../component/FlightListDetail/contact-details";
import Refund from "../refund/index";
import SubHeader from "../../component/sub-header";
import { PRIMARY_COLOR } from "../../config/constant";
import moment from "moment";
import { formatRupiah } from "./../../utilities/helpers";
import PaginationRemote from "./../../component/PaginationRemote";
import Loading from "../../component/Loading/index";

class DataFlight extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      params: {
        q: "",
        page: 1,
        limit: 20,
        sizePerPage: 20,
        totalSize: 200
      },
      selectDetail: null,
      modal: false,
      modalOrder: false,
      modalRefund: false,
      search: "",
      deleteId: null,
      deleteNotif: false,
      data: [],
      ariline_id: "",
      airlines: [
        { id: "GA", name: "Garuda", alias: "GA" },
        { id: "SJ", name: "Sriwijaya", alias: "SJ" },
        { id: "CL", name: "Citilink", alias: "CL" }
      ],
      mobile: false
    };
    this.togglePrimary = this.togglePrimary.bind(this);
    this.toggleOrder = this.toggleOrder.bind(this);
    this.toggleRefund = this.toggleRefund.bind(this);
  }
  componentWillMount() {
    this.handleResetFlightAction();
    const { q, page, limit } = this.state.params;
    this.loadDataListFlight(q, page, limit);
    this.downloadData();
    if (window.screen.availWidth < 756) {
      this.setState({ mobile: true });
    } else {
      this.setState({ mobile: false });
    }
  }
  handleResetFlightAction() {
    const { dispatch } = this.props;
    const { resetFlightAction } = this.props.actions;
    resetFlightAction(dispatch);
  }
  loadDataListFlight(query, page, limit) {
    const { dispatch } = this.props;
    let params = {
      q: query,
      page: page,
      limit: limit
    };
    const { flightAction } = this.props.actions;
    let token = !this.props.login.token ? "" : this.props.login.token;
    flightAction(dispatch, params, token);
  }
  downloadData() {
    const { dispatch } = this.props;
    let params = {
      q: "",
      page: 1,
      limit: !this.props.flight.meta ? 20 : this.props.flight.meta.total
    };
    const { flightAction1 } = this.props.actions;
    let token = !this.props.login.token ? "" : this.props.login.token;
    flightAction1(dispatch, params, token);
  }
  togglePrimary() {
    this.setState({ modal: !this.state.modal });
  }
  toggleOrder(row) {
    this.setState({ modalOrder: !this.state.modalOrder, selectDetail: row });
  }
  toggleRefund(row) {
    this.setState({ modalRefund: !this.state.modalRefund, selectDetail: row });
  }
  handleInputSearch(index) {
    const { data } = this.state;
    const { setSearching } = this.props.actionsCommon;
    let dataPost = {
      products: data[index].booking
    };
    setSearching(dataPost);
  }
  actionCol(index, row) {
    return (
      <div className="center">
        <Button
          className="button-primary"
          onClick={() => this.toggleOrder(row)}
        >
          <i className="fa fa-eye fa-lg" ></i>
        </Button>{' '}
        {/* <Button className="button-refund" onClick={() => this.toggleRefund(row)}>
          <i className="fa fa-undo fa-lg" ></i>
        </Button> */}
        {/* <Button color="primary" >EDIT</Button>{' '}
            <Button color="danger" onClick={() => this.toggleDelete(id)}>DELETE</Button> */}
      </div>
    );
  }
  renderStatusFlight(id, row) {
    return (
      <div
        className={(!row ? "" : !row.status ? "" : row.status).toLowerCase()}
      >
        {!row ? "" : !row.status ? "" : row.status}
      </div>
    );
  }
  indexList = 0;
  columns = [
    { dataField: "index", text: "NO", headerStyle: { width: "3%" } },
    {
      dataField: "booking_code",
      text: "BOOKING ID",
      headerStyle: { width: "15%" }
    },
    {
      dataField: "date",
      text: "DATE",
      headerStyle: { width: "14%" },
      formatter: (id, row) => moment(row.date).format("DD-MM-YYYY hh:mm")
    },
    { dataField: "route", text: "RUTE", headerStyle: { width: "18%" } },
    {
      dataField: "total_amount",
      text: "AMOUNT",
      headerStyle: { width: "15%" },
      formatter: (id, row) =>
        formatRupiah(!row ? 0 : !row.total_amount ? 0 : row.total_amount)
    },
    { dataField: "airline", text: "AIRLINES", headerStyle: { width: "10%" } },
    // {dataField: "source", text: "TRANSACTION", headerStyle: {width: "5%" }},
    {
      dataField: "status",
      text: "STATUS",
      headerStyle: { width: "5%" },
      formatter: this.renderStatusFlight.bind(this)
    },
    {
      dataField: "action",
      text: "ACTION",
      headerStyle: { width: "15%", textAlign: 'center' },
      formatter: (id, row) => this.actionCol(id, row)
    }
  ];

  reloadDataFlight = false;

  handleTableChange = (type, { page, sizePerPage }) => {
    let { params } = this.state;
    params.page = page;
    params.sizePerPage = sizePerPage;
    this.loadDataListFlight(params.q, params.page, params.limit);
    this.setState({ params: params });
  };
  handleChangeSearch(value) {
    let { params } = this.state;
    params.q = value;
    this.setState({ params });
    this.loadDataListFlight(params.q, params.page, params.limit);
  }
  renderLoading() {
    return (
      <div className="box-loading" style={{ marginTop: 50, marginBottom: 50 }}>
        <Loading
          type="PulseLoader"
          loading={true}
          color={"#0290d2"}
          size={25}
        />
      </div>
    );
  }

  render() {
    const { params, selectDetail, mobile } = this.state;
    const { meta = { total: 1 } } = this.props.flight;
    const { fetchingListFlight } = this.props.flight;
    let dataListFlight =
      this.props.flight.dataFlight == null ? [] : this.props.flight.dataFlight;
    let dataViewListFlight =
      this.props.flight.dataListFlight == null
        ? []
        : this.props.flight.dataListFlight;
    let index = 0;
    let no = 0;
    dataViewListFlight.map((obj, i) => {
      index++;
      obj.index =
        params.page === 1
          ? index
          : index + Number(params.page * params.sizePerPage) - 20;
    });
    dataListFlight.map((obj, i) => {
      no++;
      obj.index = no;
    });
    return (
      <div className="section-search">
        <Row className="search-flight-heading bot">Data Flight</Row>
        <Row className="step-form-table">
          <div
            style={mobile ? { width: window.innerWidth } : { width: "100%" }}
          >
            <SubHeader title={"Data Flight"} />
            <div className="form-search">
              <PaginationRemote
                download={dataListFlight == null ? false : true}
                searchPlaceholder="Search Booking Id"
                loading={fetchingListFlight}
                handleChangeSearch={this.handleChangeSearch.bind(this)}
                searchValue={params.q}
                dataexport={dataListFlight}
                data={dataViewListFlight}
                columns={this.columns}
                page={params.page}
                sizePerPage={params.sizePerPage}
                totalSize={!meta ? 1 : meta.total}
                onTableChange={this.handleTableChange}
              // id='ariline_id'
              // dataAirlines={this.state.airlines}
              />
              <Modal
                isOpen={this.state.modalRefund}
                togglePrimary={this.toggleRefund}
                className={"modal-primary" + this.props.className}
              >
                <ModalHeader style={{ background: PRIMARY_COLOR, color: "#ffffff" }} toggle={this.toggleRefund}>
                  Refund
                </ModalHeader>
                <ModalBody style={{
                  marginTop: 0,
                  marginBottom: 0,
                  paddingTop: 0,
                  paddingBottom: 0
                }}>
                  <Row><Refund selectDetail={selectDetail} /></Row>
                </ModalBody>
              </Modal>
              <Modal
                isOpen={this.state.modalOrder}
                togglePrimary={this.toggleOrder}
                className={"modal-primary" + this.props.className}
              >
                <ModalHeader
                  style={{ background: PRIMARY_COLOR, color: "#ffffff" }}
                  toggle={this.toggleOrder}
                >
                  Details Order
                </ModalHeader>
                <ModalBody
                  style={{
                    marginTop: 0,
                    marginBottom: 0,
                    paddingTop: 0,
                    paddingBottom: 0
                  }}
                >
                  <Row className="show-tab">
                    <Tabs mobile={mobile}>
                      <ContactDetail
                        selectDetail={selectDetail}
                        title="Contact Details"
                      />
                      <InfoPassenger
                        selectDetail={selectDetail}
                        title="Pessanger Information"
                      />
                    </Tabs>
                  </Row>
                </ModalBody>
              </Modal>

            </div>
            {/* } */}
          </div>
        </Row>
      </div>
    );
  }
}

export default DataFlight;
